def formatFileSize(fileSize):
  if fileSize == 0:
    return '0kb'
  if fileSize < ONE_KILO_BYTES:
    return '1kb'
  if fileSize < ONE_MEGA_BYTES:
    return '{0}kb'.format(fileSize / ONE_KILO_BYTES)
  if fileSize < ONE_GIGA_BYTES:
    return '{0}mb'.format(fileSize / ONE_MEGA_BYTES)
  return '{0}gb'.format(fileSize / ONE_GIGA_BYTES)

def formatLocalTime(dateTimeStr):
  if dateTimeStr == NEVER_TIME:
    return NEVER
  if not GC.Values[GC.TIMEZONE] or not dateTimeStr.endswith('Z'):
    return dateTimeStr
  try:
    timestamp, _ = iso8601.parse_date(dateTimeStr)
    return timestamp.astimezone(GC.Values[GC.TIMEZONE]).isoformat()
  except iso8601.ParseError:
    return dateTimeStr

def formatLocalTimestamp(timestamp):
  return datetime.datetime.fromtimestamp(int(timestamp)/1000, GC.Values[GC.TIMEZONE]).isoformat()

def formatLocalDatestamp(timestamp):
  return datetime.datetime.fromtimestamp(int(timestamp)/1000, GC.Values[GC.TIMEZONE]).strftime('%Y-%m-%d')

def formatMaxMessageBytes(maxMessageBytes):
  if maxMessageBytes < ONE_KILO_BYTES:
    return maxMessageBytes
  if maxMessageBytes < ONE_MEGA_BYTES:
    return '{0}K'.format(maxMessageBytes / ONE_KILO_BYTES)
  return '{0}M'.format(maxMessageBytes / ONE_MEGA_BYTES)

def formatMilliSeconds(millis):
  seconds, millis = divmod(millis, 1000)
  minutes, seconds = divmod(seconds, 60)
  hours, minutes = divmod(minutes, 60)
  return '%02d:%02d:%02d' % (hours, minutes, seconds)

