def convertCRsNLs(value):
  return value.replace('\r', '\\r').replace('\n', '\\n')

# Flatten a JSON object
def flattenJSON(structure, key='', path='', flattened=None, listLimit=None, timeObjects=None, noLenObjects=None):
  if flattened is None:
    flattened = {}
  if timeObjects is None:
    timeObjects = []
  if noLenObjects is None:
    noLenObjects = []
  if not isinstance(structure, (dict, list, collections.deque)):
    if key not in timeObjects:
      if isinstance(structure, str) and GC.Values[GC.CSV_OUTPUT_CONVERT_CR_NL]:
        flattened[((path+'.') if path else '')+key] = convertCRsNLs(structure)
      else:
        flattened[((path+'.') if path else '')+key] = structure
    else:
      if isinstance(structure, str):
        flattened[((path+'.') if path else '')+key] = formatLocalTime(structure)
      else:
        flattened[((path+'.') if path else '')+key] = formatLocalTimestamp(structure)
  elif isinstance(structure, (list, collections.deque)):
    listLen = len(structure)
    listLen = min(listLen, listLimit or listLen)
    if key not in noLenObjects:
      flattened[((path+'.') if path else '')+key] = listLen
    for i in range(listLen):
      flattenJSON(structure[i], '{0}'.format(i), '.'.join([item for item in [path, key] if item]), flattened, listLimit, timeObjects, noLenObjects)
  else:
    for new_key, value in list(structure.items()):
      if new_key in ['kind', 'etag']:
        continue
      flattenJSON(value, new_key, '.'.join([item for item in [path, key] if item]), flattened, listLimit, timeObjects, noLenObjects)
  return flattened

# Show a json object
def showJSON(object_name, object_value, skipObjects=None, timeObjects=None, level=0):
  if skipObjects is None:
    skipObjects = []
  if timeObjects is None:
    timeObjects = []
  if object_name in ['kind', 'etag', 'etags'] or object_name in skipObjects:
    return
  if object_name is not None:
    printJSONKey(object_name)
  if isinstance(object_value, list):
    if len(object_value) == 1 and isinstance(object_value[0], (str, int, bool)):
      if object_name is not None:
        printJSONValue(object_value[0])
      else:
        printKeyValueList([object_value[0]])
      return
    if object_name is not None:
      printBlankLine()
      Ind.Increment()
    for sub_value in object_value:
      if isinstance(sub_value, (str, int, bool)):
        printKeyValueList([sub_value])
      else:
        showJSON(None, sub_value, skipObjects, timeObjects, level+1)
    if object_name is not None:
      Ind.Decrement()
  elif isinstance(object_value, dict):
    indentAfterFirst = unindentAfterLast = False
    if object_name is not None:
      printBlankLine()
      Ind.Increment()
    elif level > 0:
      indentAfterFirst = unindentAfterLast = True
    for sub_object in sorted(object_value):
      if sub_object not in skipObjects:
        showJSON(sub_object, object_value[sub_object], skipObjects, timeObjects, level+1)
        if indentAfterFirst:
          Ind.Increment()
          indentAfterFirst = False
    if object_name is not None or unindentAfterLast:
      Ind.Decrement()
  else:
    if object_name not in timeObjects:
      if isinstance(object_value, str) and object_value.find('\n') >= 0:
        if GC.Values[GC.SHOW_CONVERT_CR_NL]:
          printJSONValue(convertCRsNLs(object_value))
        else:
          printBlankLine()
          Ind.Increment()
          printKeyValueList([Ind.MultiLineText(object_value)])
          Ind.Decrement()
      else:
        printJSONValue(object_value)
    else:
      if isinstance(object_value, str):
        printJSONValue(formatLocalTime(object_value))
      else:
        printJSONValue(formatLocalTimestamp(object_value))

