NL_SPACES_PATTERN = re.compile(r'\n +')

REPORTS_PARAMETERS_SIMPLE_TYPES = ['intValue', 'boolValue', 'datetimeValue', 'stringValue',]

REPORT_CHOICES_MAP = {
  'admin': 'admin',
  'calendar': 'calendar',
  'calendars': 'calendar',
  'customer': 'customer',
  'customers': 'customer',
  'doc': 'drive',
  'docs': 'drive',
  'domain': 'customer',
  'drive': 'drive',
  'group': 'groups',
  'groups': 'groups',
  'login': 'login',
  'logins': 'login',
  'mobile': 'mobile',
  'token': 'token',
  'tokens': 'token',
  'user': 'user',
  'users': 'user',
  }

REPORT_ACTIVITIES_TIME_OBJECTS = ['time']

# gam report <users|user> [todrive [<ToDriveAttributes>]] [nodatechange] [maxresults <Number>]
#	[date <Date>] [user all|<UserItem>] [select <UserTypeEntity>] [filter|filters <String>] [fields|parameters <String>]
# gam report <customers|customer|domain> [todrive [<ToDriveAttributes>]] [nodatechange]
#	[date <Date>] [fields|parameters <String>]
# gam report <admin|calendars|drive|docs|doc|groups|group|logins|login|mobile|tokens|token> [todrive [<ToDriveAttributes>]] [maxresults <Number>]
#	[([start <Time>] [end <Time>])|yesterday] [user all|<UserItem>] [select <UserTypeEntity>] [event <String>] [filter|filters <String>] [fields|parameters <String>] [ip <String>] countsonly summary
def doReport():

  def _adjustDate(errMsg):
    match_date = re.match('Data for dates later than (.*) is not yet available. Please check back later', errMsg)
    if not match_date:
      match_date = re.match('Start date can not be later than (.*)', errMsg)
    if (not match_date) or noDateChange:
      printWarningMessage(DATA_NOT_AVALIABLE_RC, errMsg)
      return None
    return str(match_date.group(1))

  report = getChoice(REPORT_CHOICES_MAP, mapChoice=True)
  rep = buildGAPIObject(API.REPORTS)
  customerId = GC.Values[GC.CUSTOMER_ID]
  if customerId == MY_CUSTOMER:
    customerId = None
  maxResults = try_date = filters = parameters = actorIpAddress = startTime = endTime = startDateTime = endDateTime = eventName = None
  countsOnly = exitUserLoop = noDateChange = normalizeUsers = select = summary = False
  todrive = {}
  userKey = 'all'
  filtersUserValid = report != 'customer'
  usageReports = report in ['customer', 'user']
  activityReports = not usageReports
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'todrive':
      todrive = getTodriveParameters()
    elif usageReports and myarg == 'date':
      try_date = getYYYYMMDD()
    elif usageReports and myarg == 'nodatechange':
      noDateChange = True
    elif usageReports and myarg in ['fields', 'parameters']:
      parameters = getString(Cmd.OB_STRING)
    elif activityReports and myarg == 'start':
      startDateTime, tzinfo, startTime = getFullTime(True)
      earliestDateTime = datetime.datetime.now(tzinfo)-datetime.timedelta(days=180)
      if startDateTime < earliestDateTime:
        Cmd.Backup()
        usageErrorExit(Msg.INVALID_TIME_RANGE.format('start', startTime, Msg.GOOGLE_EARLIEST_REPORT_TIME, earliestDateTime.isoformat()))
      if endDateTime and endDateTime < startDateTime:
        Cmd.Backup()
        usageErrorExit(Msg.INVALID_TIME_RANGE.format('end', endTime, 'start', startTime))
    elif activityReports and myarg == 'end':
      endDateTime, _, endTime = getFullTime(True)
      if startDateTime and endDateTime < startDateTime:
        Cmd.Backup()
        usageErrorExit(Msg.INVALID_TIME_RANGE.format('end', endTime, 'start', startTime))
    elif activityReports and myarg == 'yesterday':
      today = datetime.date.today()
      startDateTime = datetime.datetime(today.year, today.month, today.day, tzinfo=iso8601.UTC)+datetime.timedelta(days=-1)
      startTime = startDateTime.isoformat()
      endDateTime = datetime.datetime(today.year, today.month, today.day, tzinfo=iso8601.UTC)+datetime.timedelta(seconds=-1)
      endTime = endDateTime.isoformat()
    elif activityReports and myarg == 'event':
      eventName = getString(Cmd.OB_STRING)
    elif activityReports and myarg == 'ip':
      actorIpAddress = getString(Cmd.OB_STRING)
    elif activityReports and myarg == 'countsonly':
      countsOnly = True
    elif activityReports and myarg == 'summary':
      summary = True
    elif filtersUserValid and myarg == 'maxresults':
      maxResults = getInteger(minVal=1, maxVal=1000)
    elif filtersUserValid and myarg == 'user':
      userKey = getString(Cmd.OB_EMAIL_ADDRESS)
    elif filtersUserValid and myarg == 'select':
      _, users = getEntityToModify(defaultEntityType=Cmd.ENTITY_USERS)
      select = True
    elif filtersUserValid and myarg in ['filter', 'filters']:
      filters = getString(Cmd.OB_STRING)
    else:
      unknownArgumentExit()
  if try_date is None:
    try_date = str(datetime.date.today())
  if report == 'user':
    if select:
      page_message = None
      normalizeUsers = True
    elif userKey == 'all':
      printGettingAccountEntitiesInfo(Ent.USER)
      page_message = getPageMessage(showTotal=False)
      users = ['all']
    else:
      Ent.SetGetting(Ent.USER)
      page_message = getPageMessage(showTotal=False)
      users = [normalizeEmailAddressOrUID(userKey)]
    titles, csvRows = initializeTitlesCSVfile(['email', 'date'])
    i = 0
    count = len(users)
    for user in users:
      i += 1
      if normalizeUsers:
        user = normalizeEmailAddressOrUID(user)
      while True:
        try:
          feed = callGAPIpages(rep.userUsageReport(), 'get', 'usageReports',
                               page_message=page_message,
                               throw_reasons=[GAPI.INVALID, GAPI.BAD_REQUEST, GAPI.FORBIDDEN],
                               date=try_date, userKey=user, customerId=customerId, filters=filters, parameters=parameters,
                               maxResults=maxResults)
          while feed:
            user_report = feed.popleft()
            if 'entity' not in user_report:
              continue
            row = {'email': user_report['entity']['userEmail'], 'date': try_date}
            for item in user_report.get('parameters', {}):
              name = item['name']
              if name not in titles['set']:
                addTitleToCSVfile(name, titles)
              for ptype in REPORTS_PARAMETERS_SIMPLE_TYPES:
                if ptype in item:
                  if ptype != 'datetimeValue':
                    row[name] = item[ptype]
                  else:
                    row[name] = formatLocalTime(item[ptype])
                  break
              else:
                row[name] = ''
            csvRows.append(row)
          break
        except GAPI.invalid as e:
          try_date = _adjustDate(e.message)
          if not try_date:
            return
        except GAPI.badRequest:
          if user != 'all':
            entityUnknownWarning(Ent.USER, user, i, count)
          else:
            printErrorMessage(BAD_REQUEST_RC, Msg.BAD_REQUEST)
            exitUserLoop = True
          break
        except GAPI.forbidden:
          accessErrorExit(None)
      if exitUserLoop:
        break
    sortCSVTitles(['email', 'date'], titles)
    writeCSVfile(csvRows, titles, 'User Reports - {0}'.format(try_date), todrive)
  elif report == 'customer':
    titles, csvRows = initializeTitlesCSVfile(['name', 'value', 'client_id'])
    auth_apps = []
    while True:
      try:
        usage = callGAPIpages(rep.customerUsageReports(), 'get', 'usageReports',
                              throw_reasons=[GAPI.INVALID, GAPI.FORBIDDEN],
                              customerId=customerId, date=try_date, parameters=parameters)
        for item in usage[0]['parameters']:
          name = item['name']
          for ptype in REPORTS_PARAMETERS_SIMPLE_TYPES:
            if ptype in item:
              if ptype != 'datetimeValue':
                csvRows.append({'name': name, 'value': item[ptype]})
              else:
                csvRows.append({'name': name, 'value': formatLocalTime(item[ptype])})
              break
          else:
            if 'msgValue' in item:
              if name == 'accounts:authorized_apps':
                for subitem in item['msgValue']:
                  app = {}
                  for an_item in subitem:
                    if an_item == 'client_name':
                      app['name'] = 'App: {0}'.format(convertCRsNLs(subitem[an_item]))
                    elif an_item == 'num_users':
                      app['value'] = '{0} users'.format(subitem[an_item])
                    elif an_item == 'client_id':
                      app['client_id'] = subitem[an_item]
                  auth_apps.append(app)
        for row in auth_apps: # put apps at bottom
          csvRows.append(row)
        break
      except GAPI.invalid as e:
        try_date = _adjustDate(e.message)
        if not try_date:
          return
      except GAPI.forbidden:
        accessErrorExit(None)
    writeCSVfile(csvRows, titles, 'Customer Report - {0}'.format(try_date), todrive)
  else:     # admin, calendar, drive, groups, login, mobile, token
    if select:
      page_message = None
      normalizeUsers = True
    elif userKey == 'all':
      printGettingAccountEntitiesInfo(Ent.ACTIVITY)
      page_message = getPageMessage(showTotal=False)
      users = ['all']
    else:
      Ent.SetGetting(Ent.ACTIVITY)
      page_message = getPageMessage(showTotal=False)
      users = [normalizeEmailAddressOrUID(userKey)]
    eventCounts = {}
    titles, csvRows = initializeTitlesCSVfile(None)
    i = 0
    count = len(users)
    for user in users:
      i += 1
      if normalizeUsers:
        user = normalizeEmailAddressOrUID(user)
      try:
        feed = callGAPIpages(rep.activities(), 'list', 'items',
                             page_message=page_message,
                             throw_reasons=[GAPI.BAD_REQUEST, GAPI.INVALID, GAPI.AUTH_ERROR],
                             applicationName=report, userKey=user, customerId=customerId,
                             actorIpAddress=actorIpAddress, startTime=startTime, endTime=endTime, eventName=eventName, filters=filters,
                             maxResults=maxResults)
        while feed:
          activity = feed.popleft()
          events = activity['events']
          del activity['events']
          if not countsOnly:
            activity_row = flattenJSON(activity, timeObjects=REPORT_ACTIVITIES_TIME_OBJECTS)
            for event in events:
              for item in event.get('parameters', []):
                if item['name'] in ['start_time', 'end_time']:
                  val = item.get('intValue')
                  if val is not None:
                    item['dateTimeValue'] = datetime.datetime.fromtimestamp(int(val)-62135683200, GC.Values[GC.TIMEZONE]).isoformat()
                    item.pop('intValue')
                if 'value' in item:
                  item['value'] = NL_SPACES_PATTERN.sub('', item['value'])
              row = flattenJSON(event)
              row.update(activity_row)
              addRowTitlesToCSVfile(row, csvRows, titles)
          elif not summary:
            actor = activity['actor']['email']
            eventCounts.setdefault(actor, {})
            for event in events:
              eventCounts[actor].setdefault(event['name'], 0)
              eventCounts[actor][event['name']] += 1
          else:
            for event in events:
              eventCounts.setdefault(event['name'], 0)
              eventCounts[event['name']] += 1
      except GAPI.badRequest:
        if user != 'all':
          entityUnknownWarning(Ent.USER, user, i, count)
        else:
          printErrorMessage(BAD_REQUEST_RC, Msg.BAD_REQUEST)
          break
      except GAPI.invalid as e:
        systemErrorExit(GOOGLE_API_ERROR_RC, e.message)
      except GAPI.authError:
        accessErrorExit(None)
    if not countsOnly:
      sortCSVTitles(['name',], titles)
    elif not summary:
      addTitlesToCSVfile(['emailAddress',], titles)
      for actor, events in list(eventCounts.items()):
        row = {'emailAddress': actor}
        for event, count in list(events.items()):
          row[event] = count
        addRowTitlesToCSVfile(row, csvRows, titles)
      sortCSVTitles(['emailAddress',], titles)
    else:
      addTitlesToCSVfile(['event', 'count'], titles)
      for event in sorted(eventCounts):
        csvRows.append({'event': event, 'count': eventCounts[event]})
    writeCSVfile(csvRows, titles, '{0} Activity Report'.format(report.capitalize()), todrive)

ADDRESS_FIELDS_PRINT_ORDER = ['contactName', 'organizationName', 'addressLine1', 'addressLine2', 'addressLine3', 'locality', 'region', 'postalCode', 'countryCode']

