# gam <CrOSTypeEntity> update <CrOSAttributes>
def updateCrOSDevices(entityList, cd=None):
  if not cd:
    cd = buildGAPIObject(API.DIRECTORY)
  update_body = {}
  action_body = {}
  ack_wipe = False
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg in UPDATE_CROS_ARGUMENT_TO_PROPERTY_MAP:
      up = UPDATE_CROS_ARGUMENT_TO_PROPERTY_MAP[myarg]
      if up == 'orgUnitPath':
        update_body[up] = getOrgUnitItem(pathOnly=True)
      elif up == 'notes':
        update_body[up] = getString(Cmd.OB_STRING, minLen=0).replace('\\n', '\n')
      else:
        update_body[up] = getString(Cmd.OB_STRING, minLen=[0, 1][up == 'annotatedAssetId'])
    elif myarg == 'action':
      action_body['action'], deprovisionReason = getChoice(CROS_ACTION_CHOICES_MAP, mapChoice=True)
      if deprovisionReason:
        action_body['deprovisionReason'] = deprovisionReason
      Act.Set(CROS_ACTION_NAME_MAP[action_body['action']])
    elif myarg == 'acknowledgedevicetouchrequirement':
      ack_wipe = True
    else:
      unknownArgumentExit()
  i, count, entityList = getEntityArgument(entityList)
  if action_body:
    if action_body['action'] == 'deprovision' and not ack_wipe:
      stderrWarningMsg(Msg.REFUSING_TO_DEPROVISION_DEVICES.format(count))
      systemErrorExit(AC_NOT_PERFORMED_RC, None)
    function = 'action'
    parmId = 'resourceId'
    kwargs = {parmId: None, 'body': action_body}
  elif update_body:
    function = 'patch'
    parmId = 'deviceId'
    kwargs = {parmId: None, 'body': update_body, 'fields': ''}
  else:
    return
  for deviceId in entityList:
    i += 1
    kwargs[parmId] = deviceId
    try:
      callGAPI(cd.chromeosdevices(), function,
               throw_reasons=[GAPI.INVALID, GAPI.INVALID_ORGUNIT, GAPI.BAD_REQUEST, GAPI.RESOURCE_NOT_FOUND, GAPI.FORBIDDEN],
               customerId=GC.Values[GC.CUSTOMER_ID], **kwargs)
      entityActionPerformed([Ent.CROS_DEVICE, deviceId], i, count)
    except GAPI.invalid as e:
      entityActionFailedWarning([Ent.CROS_DEVICE, deviceId], e.message, i, count)
    except GAPI.invalidOrgunit:
      entityActionFailedWarning([Ent.CROS_DEVICE, deviceId], Msg.INVALID_ORGUNIT, i, count)
    except (GAPI.badRequest, GAPI.resourceNotFound, GAPI.forbidden):
      checkEntityAFDNEorAccessErrorExit(cd, Ent.CROS_DEVICE, deviceId, i, count)

