# gam <CrOSTypeEntity> info [nolists] [listlimit <Number>]
#	[basic|full|allfields] <CrOSFieldName>* [fields <CrOSFieldNameList>]
def infoCrOSDevices(entityList, cd=None):
  if not cd:
    cd = buildGAPIObject(API.DIRECTORY)
  projection = None
  fieldsList = []
  noLists = False
  listLimit = 0
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'nolists':
      noLists = True
    elif myarg == 'listlimit':
      listLimit = getInteger(minVal=0)
    elif myarg == 'allfields':
      projection = 'FULL'
      fieldsList = []
    elif myarg in PROJECTION_CHOICES_MAP:
      projection = PROJECTION_CHOICES_MAP[myarg]
      if projection == 'FULL':
        fieldsList = []
      else:
        fieldsList = CROS_BASIC_FIELDS_LIST[:]
    elif myarg in CROS_ARGUMENT_TO_PROPERTY_MAP:
      if not fieldsList:
        fieldsList = ['deviceId',]
      fieldsList.extend(CROS_ARGUMENT_TO_PROPERTY_MAP[myarg])
    elif myarg == 'fields':
      if not fieldsList:
        fieldsList = ['deviceId',]
      fieldNameList = getString(Cmd.OB_FIELD_NAME_LIST)
      for field in fieldNameList.lower().replace(',', ' ').split():
        if field in CROS_ARGUMENT_TO_PROPERTY_MAP:
          fieldsList.extend(CROS_ARGUMENT_TO_PROPERTY_MAP[field])
          if field in ['recentusers', 'timeranges', 'activetimeranges']:
            projection = 'FULL'
            noLists = False
        else:
          Cmd.Backup()
          invalidChoiceExit(CROS_ARGUMENT_TO_PROPERTY_MAP)
    else:
      unknownArgumentExit()
  fields = ','.join(set(fieldsList)).replace('.', '/') if fieldsList else None
  i, count, entityList = getEntityArgument(entityList)
  for deviceId in entityList:
    i += 1
    try:
      cros = callGAPI(cd.chromeosdevices(), 'get',
                      throw_reasons=[GAPI.BAD_REQUEST, GAPI.RESOURCE_NOT_FOUND, GAPI.FORBIDDEN],
                      customerId=GC.Values[GC.CUSTOMER_ID], deviceId=deviceId, projection=projection, fields=fields)
      printEntity([Ent.CROS_DEVICE, deviceId], i, count)
      Ind.Increment()
      if 'notes' in cros:
        cros['notes'] = convertCRsNLs(cros['notes'])
      for up in CROS_SCALAR_PROPERTY_PRINT_ORDER:
        if up in cros:
          if up not in CROS_TIME_OBJECTS:
            printKeyValueList([up, cros[up]])
          else:
            printKeyValueList([up, formatLocalTime(cros[up])])
      if not noLists:
        activeTimeRanges = cros.get('activeTimeRanges', [])
        lenATR = len(activeTimeRanges)
        if lenATR:
          printKeyValueList(['activeTimeRanges'])
          Ind.Increment()
          for i in range(min(lenATR, listLimit or lenATR)):
            printKeyValueList(['date', activeTimeRanges[i]['date']])
            Ind.Increment()
            printKeyValueList(['activeTime', str(activeTimeRanges[i]['activeTime'])])
            printKeyValueList(['duration', formatMilliSeconds(activeTimeRanges[i]['activeTime'])])
            Ind.Decrement()
          Ind.Decrement()
        recentUsers = cros.get('recentUsers', [])
        lenRU = len(recentUsers)
        if lenRU:
          printKeyValueList(['recentUsers'])
          Ind.Increment()
          for i in range(min(lenRU, listLimit or lenRU)):
            printKeyValueList(['type', recentUsers[i]['type']])
            Ind.Increment()
            printKeyValueList(['email', recentUsers[i].get('email', '')])
            Ind.Decrement()
          Ind.Decrement()
      Ind.Decrement()
    except (GAPI.badRequest, GAPI.resourceNotFound, GAPI.forbidden):
      checkEntityAFDNEorAccessErrorExit(cd, Ent.CROS_DEVICE, deviceId, i, count)

