def getGroupAttrValue(argument, gs_body):
  attrProperties = GROUP_ATTRIBUTES.get(argument)
  if not attrProperties:
    unknownArgumentExit()
  attrName = attrProperties[0]
  attribute = attrProperties[1]
  attrType = attribute[GC.VAR_TYPE]
  if attrType == GC.TYPE_BOOLEAN:
    gs_body[attrName] = getBoolean()
  elif attrType == GC.TYPE_STRING:
    if attrName in GROUP_FIELDS_WITH_CRS_NLS:
      gs_body[attrName] = getString(Cmd.OB_STRING, minLen=0).replace('\\n', '\n')
    else:
      gs_body[attrName] = getString(Cmd.OB_STRING, minLen=0)
  elif attrType == GC.TYPE_CHOICE:
    gs_body[attrName] = getChoice(attribute['choices'], mapChoice=True)
  elif attrType == GC.TYPE_EMAIL:
    gs_body[attrName] = getEmailAddress(noUid=True)
  elif attrType == GC.TYPE_LANGUAGE:
    gs_body[attrName] = getChoice(LANGUAGE_CODES_MAP, mapChoice=True)
  else: # GC.TYPE_INTEGER
    if attrName == 'maxMessageBytes':
      gs_body[attrName] = getMaxMessageBytes()
    else:
      gs_body[attrName] = getInteger()

def GroupIsAbuseOrPostmaster(emailAddr):
  return emailAddr.startswith('abuse@') or emailAddr.startswith('postmaster@')

# gam create group <EmailAddress> <GroupAttributes>
def doCreateGroup():
  cd = buildGAPIObject(API.DIRECTORY)
  body = {'email': getEmailAddress(noUid=True)}
  gs_body = {}
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'name':
      body['name'] = getString(Cmd.OB_STRING)
    elif myarg == 'description':
      body['description'] = getString(Cmd.OB_STRING, minLen=0).replace('\\n', '\n')
    else:
      getGroupAttrValue(myarg, gs_body)
  body.setdefault('name', body['email'])
  try:
    callGAPI(cd.groups(), 'insert',
             throw_reasons=[GAPI.DUPLICATE, GAPI.DOMAIN_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INVALID, GAPI.INVALID_INPUT],
             body=body, fields='')
    errMsg = ''
    if gs_body and not GroupIsAbuseOrPostmaster(body['email']):
      gs = buildGAPIObject(API.GROUPSSETTINGS)
      result = callGAPI(gs.groups(), 'patch',
                        soft_errors=True, throw_reasons=GAPI.GROUP_SETTINGS_THROW_REASONS, retry_reasons=GAPI.GROUP_SETTINGS_RETRY_REASONS,
                        groupUniqueId=body['email'], body=gs_body, fields='')
      if result is None:
        errMsg = Msg.API_ERROR_SETTINGS
    entityActionPerformedMessage([Ent.GROUP, body['email']], errMsg)
  except GAPI.duplicate:
    entityDuplicateWarning(Ent.GROUP, body['email'])
  except (GAPI.groupNotFound, GAPI.domainNotFound, GAPI.domainCannotUseApis, GAPI.forbidden, GAPI.backendError, GAPI.systemError, GAPI.invalid, GAPI.invalidInput) as e:
    entityActionFailedWarning([Ent.GROUP, body['email']], e.message)

