INFO_GROUP_OPTIONS = ['nousers', 'groups',]

def infoGroups(entityList):
  cd = buildGAPIObject(API.DIRECTORY)
  getAliases = getUsers = True
  getGroups = getSettings = False
  formatJSON = False
  groups = collections.deque()
  members = collections.deque()
  cdfieldsList = gsfieldsList = None
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'nousers':
      getUsers = False
    elif myarg == 'noaliases':
      getAliases = False
    elif myarg == 'groups':
      getGroups = True
    elif myarg in GROUP_ARGUMENT_TO_PROPERTY_TITLE_MAP:
      if not cdfieldsList:
        cdfieldsList = ['email',]
      cdfieldsList.extend([GROUP_ARGUMENT_TO_PROPERTY_TITLE_MAP[myarg][0],])
    elif myarg in GROUP_ATTRIBUTES:
      if not gsfieldsList:
        gsfieldsList = []
      gsfieldsList.extend([GROUP_ATTRIBUTES[myarg][0],])
    elif myarg == 'fields':
      if not cdfieldsList:
        cdfieldsList = ['email',]
      if not gsfieldsList:
        gsfieldsList = []
      fieldNameList = getString(Cmd.OB_FIELD_NAME_LIST)
      for field in fieldNameList.lower().replace(',', ' ').split():
        if field in GROUP_ARGUMENT_TO_PROPERTY_TITLE_MAP:
          cdfieldsList.extend([GROUP_ARGUMENT_TO_PROPERTY_TITLE_MAP[field][0],])
        elif field in GROUP_ATTRIBUTES:
          gsfieldsList.extend([GROUP_ATTRIBUTES[field][0],])
        else:
          Cmd.Backup()
          invalidChoiceExit(list(GROUP_ARGUMENT_TO_PROPERTY_TITLE_MAP.keys())+list(GROUP_ATTRIBUTES.keys()))
# Ignore info user arguments that may have come from whatis
    elif myarg in INFO_USER_OPTIONS:
      if myarg == 'schemas':
        getString(Cmd.OB_SCHEMA_NAME_LIST)
    elif myarg == "formatjson":
      formatJSON = True
    else:
      unknownArgumentExit()
  cdfields = ','.join(set(cdfieldsList)) if cdfieldsList else None
  if gsfieldsList is None:
    getSettings = True
    gsfields = None
  elif gsfieldsList:
    getSettings = True
    gsfields = ','.join(set(gsfieldsList))
  else:
    gsfields = None
  if getSettings:
    gs = buildGAPIObject(API.GROUPSSETTINGS)
  i = 0
  count = len(entityList)
  for group in entityList:
    i += 1
    group = normalizeEmailAddressOrUID(group)
    try:
      basic_info = callGAPI(cd.groups(), 'get',
                            throw_reasons=GAPI.GROUP_GET_THROW_REASONS, retry_reasons=GAPI.GROUP_GET_RETRY_REASONS,
                            groupKey=group, fields=cdfields)
      group = basic_info['email']
      settings = {}
      if getSettings and not GroupIsAbuseOrPostmaster(group):
        settings = callGAPI(gs.groups(), 'get',
                            soft_errors=True, throw_reasons=GAPI.GROUP_SETTINGS_THROW_REASONS, retry_reasons=GAPI.GROUP_SETTINGS_RETRY_REASONS,
                            groupUniqueId=group, fields=gsfields) # Use email address retrieved from cd since GS API doesn't support uid
      if getGroups:
        groups = callGAPIpages(cd.groups(), 'list', 'groups',
                               userKey=group, fields='nextPageToken,groups(name,email)')
      if getUsers:
        members = callGAPIpages(cd.members(), 'list', 'members',
                                throw_reasons=GAPI.MEMBERS_THROW_REASONS, retry_reasons=GAPI.MEMBERS_RETRY_REASONS,
                                groupKey=group, fields='nextPageToken,members(email,id,role,status,type)', maxResults=GC.Values[GC.MEMBER_MAX_RESULTS])
      if formatJSON:
        basic_info.update(settings)
        if getGroups:
          basic_info['groups'] = list(groups)
        if getUsers:
          basic_info['members'] = list(members)
        printLine(json.dumps(basic_info, ensure_ascii=False, sort_keys=True))
        continue
      printEntity([Ent.GROUP, group], i, count)
      Ind.Increment()
      printEntity([Ent.GROUP_SETTINGS, None])
      Ind.Increment()
      for key, value in list(basic_info.items()):
        if key in ['kind', 'etag', 'email', 'aliases']:
          continue
        if isinstance(value, list):
          printKeyValueList([key, None])
          Ind.Increment()
          for val in value:
            printKeyValueList([val])
          Ind.Decrement()
        else:
          if key in GROUP_FIELDS_WITH_CRS_NLS:
            value = convertCRsNLs(value)
          printKeyValueList([key, value])
      if settings:
        for key, value in sorted(settings.items()):
          if key in ['kind', 'etag', 'email', 'name', 'description']:
            continue
          if key == 'maxMessageBytes':
            value = formatMaxMessageBytes(value)
          elif key in GROUP_FIELDS_WITH_CRS_NLS:
            value = convertCRsNLs(value)
          printKeyValueList([key, value])
      Ind.Decrement()
      if getAliases:
        aliases = basic_info.get('aliases', [])
        if aliases:
          printEntitiesCount(Ent.EMAIL_ALIAS, aliases)
          Ind.Increment()
          for alias in aliases:
            printKeyValueList([alias])
          Ind.Decrement()
      if getGroups:
        printEntitiesCount(Ent.GROUP, groups)
        Ind.Increment()
        for groupm in groups:
          printKeyValueList([groupm['name'], groupm['email']])
        Ind.Decrement()
      if getUsers:
        printEntitiesCount(Ent.MEMBER, members)
        Ind.Increment()
        for member in members:
          printKeyValueList([member.get('role', Ent.ROLE_MEMBER).lower(), '{0} ({1})'.format(member.get('email', member['id']), member['type'].lower())])
        Ind.Decrement()
        printKeyValueList(['Total users in group', len(members)])
      Ind.Decrement()
    except (GAPI.groupNotFound, GAPI.domainNotFound, GAPI.invalid, GAPI.forbidden, GAPI.badRequest, GAPI.backendError, GAPI.systemError):
      entityUnknownWarning(Ent.GROUP, group, i, count)

