def getGroupMembers(cd, groupEmail, membersList, membersSet, i, count, noduplicates, recursive, level):
  try:
    printGettingAllEntityItemsForWhom(Ent.MEMBER, groupEmail, i, count)
    groupMembers = callGAPIpages(cd.members(), 'list', 'members',
                                 throw_reasons=GAPI.MEMBERS_THROW_REASONS, retry_reasons=GAPI.MEMBERS_RETRY_REASONS,
                                 groupKey=groupEmail, fields='nextPageToken,members(email,id,role,status,type)', maxResults=GC.Values[GC.MEMBER_MAX_RESULTS])
    if not recursive:
      if noduplicates:
        for member in groupMembers:
          if member['id'] in membersSet:
            continue
          membersSet.add(member['id'])
          membersList.append(member)
      else:
        membersList.extend(groupMembers)
    else:
      for member in groupMembers:
        if member['type'] == 'USER':
          if noduplicates:
            if member['id'] in membersSet:
              continue
            membersSet.add(member['id'])
          member['level'] = level
          member['subgroup'] = groupEmail
          membersList.append(member)
        elif member['type'] == 'GROUP':
          getGroupMembers(cd, member['email'], membersList, membersSet, i, count, noduplicates, recursive, level+1)
  except (GAPI.groupNotFound, GAPI.domainNotFound, GAPI.invalid, GAPI.forbidden):
    entityUnknownWarning(Ent.GROUP, groupEmail, i, count)

