  showPermissions = False
  acls = None
  fieldsList = []
  if csvFormat:
    todrive = {}
    titles, csvRows = initializeTitlesCSVfile(None)
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if csvFormat and myarg == 'todrive':
      todrive = getTodriveParameters()
    elif myarg == 'allfields':
      fieldsList = RESOURCE_ALLFIELDS
    elif myarg in [Cmd.ARG_ACLS, Cmd.ARG_CALENDARACLS, Cmd.ARG_PERMISSIONS]:
      showPermissions = True
    elif myarg in RESOURCE_ARGUMENT_TO_PROPERTY_MAP:
      fieldsList.extend(RESOURCE_ARGUMENT_TO_PROPERTY_MAP[myarg])
    elif myarg == 'fields':
      for field in getString(Cmd.OB_FIELD_NAME_LIST).lower().replace(',', ' ').split():
        if field == 'acls':
          showPermissions = True
        elif field in RESOURCE_ARGUMENT_TO_PROPERTY_MAP:
          fieldsList.extend(RESOURCE_ARGUMENT_TO_PROPERTY_MAP[field])
        else:
          Cmd.Backup()
          invalidChoiceExit(RESOURCE_ARGUMENT_TO_PROPERTY_MAP)
    elif myarg in ['convertcrnl', 'converttextnl']:
      convertCRNL = True
    else:
      unknownArgumentExit()
  if not fieldsList:
    fieldsList = RESOURCE_ALLFIELDS
  if csvFormat:
    addTitlesToCSVfile(fieldsList, titles)
  if showPermissions:
    cal = buildGAPIObject(API.CALENDAR)
    fields = 'nextPageToken,items({0})'.format(','.join(set(fieldsList+['resourceEmail',])))
  else:
    fields = 'nextPageToken,items({0})'.format(','.join(set(fieldsList)))
  printGettingAccountEntitiesInfo(Ent.RESOURCE_CALENDAR)
  try:
    page_message = getPageMessage(showTotal=False, showFirstLastItems=True)
    resources = callGAPIpages(cd.resources().calendars(), 'list', 'items',
                              page_message=page_message, message_attribute='resourceName',
                              throw_reasons=[GAPI.BAD_REQUEST, GAPI.RESOURCE_NOT_FOUND, GAPI.FORBIDDEN],
                              customer=GC.Values[GC.CUSTOMER_ID], fields=fields)
    i = 0
    count = len(resources)
    for resource in resources:
      i += 1
      if showPermissions:
        acls = callGAPIpages(cal.acl(), 'list', 'items',
                             throw_reasons=[GAPI.NOT_FOUND, GAPI.FORBIDDEN],
                             calendarId=resource['resourceEmail'], fields='nextPageToken,items(id,role,scope)')
      if not csvFormat:
        _showResource(resource, i, count, acls)
      else:
        row = {}
        for field in fieldsList:
          if convertCRNL and field in RESOURCE_FIELDS_WITH_CRS_NLS:
            row[field] = convertCRsNLs(resource.get(field, ''))
          else:
            row[field] = resource.get(field, '')
        if showPermissions:
          for rule in acls:
            addRowTitlesToCSVfile(flattenJSON(rule, flattened=row.copy()), csvRows, titles)
        else:
          csvRows.append(row)
  except (GAPI.badRequest, GAPI.resourceNotFound, GAPI.forbidden):
    accessErrorExit(cd)
  if csvFormat:
    sortCSVTitles(RESOURCE_ALLFIELDS, titles)
    writeCSVfile(csvRows, titles, 'Resources', todrive)

