# gam <UserTypeEntity> update user <UserAttributes>
def updateUsers(entityList):
  cd = buildGAPIObject(API.DIRECTORY)
  body, admin_body, createIfNotFound = getUserAttributes(cd, True)
  i, count, entityList = getEntityArgument(entityList)
  for user in entityList:
    i += 1
    user = normalizeEmailAddressOrUID(user)
    try:
      if ('primaryEmail' in body) and (body['primaryEmail'][:4].lower() == 'vfe@'):
        vfe = True
        user_primary = callGAPI(cd.users(), 'get',
                                throw_reasons=GAPI.USER_GET_THROW_REASONS,
                                userKey=user, fields='primaryEmail,id')
        user = user_primary['id']
        user_primary = user_primary['primaryEmail']
        user_name, user_domain = splitEmailAddress(user_primary)
        body['primaryEmail'] = 'vfe.{0}.{1:05}@{2}'.format(user_name, random.randint(1, 99999), user_domain)
        body['emails'] = [{'type': 'custom',
                           'customType': 'former_employee',
                           'primary': False, 'address': user_primary}]
      else:
        vfe = False
      if body:
        try:
          callGAPI(cd.users(), 'update',
                   throw_reasons=[GAPI.USER_NOT_FOUND, GAPI.DOMAIN_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INVALID, GAPI.INVALID_ORGUNIT, GAPI.INVALID_SCHEMA_VALUE],
                   userKey=user, body=body, fields='')
          entityActionPerformed([Ent.USER, user], i, count)
        except GAPI.userNotFound:
          if createIfNotFound and (count == 1) and not vfe:
            if 'primaryEmail' not in body:
              body['primaryEmail'] = user
            try:
              callGAPI(cd.users(), 'insert',
                       throw_reasons=[GAPI.DUPLICATE, GAPI.DOMAIN_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INVALID, GAPI.INVALID_ORGUNIT, GAPI.INVALID_SCHEMA_VALUE],
                       body=body, fields='')
              Act.Set(Act.CREATE)
              entityActionPerformed([Ent.USER, user], i, count)
            except GAPI.duplicate:
              entityDuplicateWarning(Ent.USER, user, i, count)
          else:
            entityUnknownWarning(Ent.USER, user, i, count)
            continue
      if admin_body:
        changeAdminStatus(cd, user, admin_body, i, count)
    except (GAPI.userNotFound, GAPI.domainNotFound, GAPI.domainCannotUseApis, GAPI.forbidden, GAPI.badRequest, GAPI.backendError, GAPI.systemError):
      entityUnknownWarning(Ent.USER, user, i, count)
    except GAPI.invalidSchemaValue:
      entityActionFailedWarning([Ent.USER, user], Msg.INVALID_SCHEMA_VALUE, i, count)
    except GAPI.invalid as e:
      entityActionFailedWarning([Ent.USER, user], e.message, i, count)
    except GAPI.invalidOrgunit:
      entityActionFailedWarning([Ent.USER, user], Msg.INVALID_ORGUNIT, i, count)

