# gam [<UserTypeEntity>] print users [todrive [<ToDriveAttributes>]] ([domain <DomainName>] [query <QueryUsers>] [deleted_only|only_deleted])|[select <UserTypeEntity>]
#	[groups] [license|licenses|licence|licences] [emailpart|emailparts|username] [schemas|custom all|<SchemaNameList>]
#	[orderby <UserOrderByFieldName> [ascending|descending]] [userview]
#	[basic|full|allfields | <UserFieldName>* | fields <UserFieldNameList>] [delimiter <String>]
def doPrintUsers(entityList=None):
  def _printUser(userEntity):
    if email_parts and ('primaryEmail' in userEntity):
      userEmail = userEntity['primaryEmail']
      if userEmail.find('@') != -1:
        userEntity['primaryEmailLocal'], userEntity['primaryEmailDomain'] = splitEmailAddress(userEmail)
    addRowTitlesToCSVfile(flattenJSON(userEntity, timeObjects=USER_TIME_OBJECTS), csvRows, titles)

  _PRINT_USER_REASON_TO_MESSAGE_MAP = {GAPI.RESOURCE_NOT_FOUND: Msg.DOES_NOT_EXIST}
  def _callbackPrintUser(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is None:
      _printUser(response)
    else:
      http_status, reason, message = checkGAPIError(exception)
      if reason in GAPI.USER_GET_THROW_REASONS:
        entityUnknownWarning(Ent.USER, ri[RI_ITEM], int(ri[RI_J]), int(ri[RI_JCOUNT]))
      elif (reason == GAPI.INVALID_INPUT) and customFieldMask:
        entityActionFailedWarning([Ent.USER, ri[RI_ITEM]], invalidUserSchema(customFieldMask), int(ri[RI_J]), int(ri[RI_JCOUNT]))
      else:
        errMsg = getHTTPError(_PRINT_USER_REASON_TO_MESSAGE_MAP, http_status, reason, message)
        printKeyValueList([ERROR, errMsg])

  cd = buildGAPIObject(API.DIRECTORY)
  todrive = {}
  fieldsList = []
  fieldsTitles = {}
  titles, csvRows = initializeTitlesCSVfile(None)
  addFieldToCSVfile('primaryemail', USER_ARGUMENT_TO_PROPERTY_MAP, fieldsList, fieldsTitles, titles)
  sortHeaders = getGroupFeed = getLicenseFeed = email_parts = False
  customer = GC.Values[GC.CUSTOMER_ID]
  domain = None
  query = None
  projection = 'basic'
  customFieldMask = None
  viewType = deleted_only = orderBy = sortOrder = None
  delimiter = GC.Values[GC.CSV_OUTPUT_FIELD_DELIMITER]
  select = selectLookup = False
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'todrive':
      todrive = getTodriveParameters()
    elif myarg == 'domain':
      domain = getString(Cmd.OB_DOMAIN_NAME).lower()
      customer = None
    elif myarg == 'query':
      query = getString(Cmd.OB_QUERY)
    elif myarg in ['deletedonly', 'onlydeleted']:
      deleted_only = True
    elif myarg == 'select':
      _, entityList = getEntityToModify(defaultEntityType=Cmd.ENTITY_USERS)
      select = True
    elif myarg == 'orderby':
      orderBy = getChoice(USERS_ORDERBY_CHOICES_MAP, mapChoice=True)
      sortOrder = getChoice(SORTORDER_CHOICES_MAP, defaultChoice='ascending', mapChoice=True)
    elif myarg == 'userview':
      viewType = 'domain_public'
    elif myarg in ['custom', 'schemas']:
      if not fieldsList:
        fieldsList = ['primaryEmail',]
      fieldsList.append('customSchemas')
      customFieldMask = getString(Cmd.OB_SCHEMA_NAME_LIST).replace(' ', ',')
      if customFieldMask.lower() == 'all':
        customFieldMask = None
        projection = 'full'
      else:
        projection = 'custom'
    elif myarg == 'delimiter':
      delimiter = getDelimiter()
    elif myarg in PROJECTION_CHOICES_MAP:
      projection = myarg
      sortHeaders = True
      fieldsList = []
    elif myarg == 'allfields':
      projection = 'basic'
      sortHeaders = True
      fieldsList = []
    elif myarg in USER_ARGUMENT_TO_PROPERTY_MAP:
      if not fieldsList:
        fieldsList = ['primaryEmail',]
      addFieldToCSVfile(myarg, USER_ARGUMENT_TO_PROPERTY_MAP, fieldsList, fieldsTitles, titles)
    elif myarg == 'fields':
      if not fieldsList:
        fieldsList = ['primaryEmail',]
      fieldNameList = getString(Cmd.OB_FIELD_NAME_LIST)
      for field in fieldNameList.lower().replace(',', ' ').split():
        if field in USER_ARGUMENT_TO_PROPERTY_MAP:
          addFieldToCSVfile(field, USER_ARGUMENT_TO_PROPERTY_MAP, fieldsList, fieldsTitles, titles)
        else:
          Cmd.Backup()
          invalidChoiceExit(USER_ARGUMENT_TO_PROPERTY_MAP)
    elif myarg == 'groups':
      getGroupFeed = True
    elif myarg in ['license', 'licenses', 'licence', 'licences']:
      getLicenseFeed = True
    elif myarg in ['emailpart', 'emailparts', 'username']:
      email_parts = True
    else:
      unknownArgumentExit()
  _, _, entityList = getEntityArgument(entityList)
  if entityList is None:
    fields = 'nextPageToken,users({0})'.format(','.join(set(fieldsList))).replace('.', '/') if fieldsList else None
    printGettingAccountEntitiesInfo(Ent.USER, qualifier=queryQualifier(query))
    page_message = getPageMessage(showFirstLastItems=True)
    try:
      feed = callGAPIpages(cd.users(), 'list', 'users',
                           page_message=page_message, message_attribute='primaryEmail',
                           throw_reasons=[GAPI.DOMAIN_NOT_FOUND, GAPI.INVALID_INPUT, GAPI.BAD_REQUEST, GAPI.RESOURCE_NOT_FOUND, GAPI.FORBIDDEN],
                           customer=customer, domain=domain, fields=fields, query=query,
                           showDeleted=deleted_only, orderBy=orderBy, sortOrder=sortOrder, viewType=viewType,
                           projection=projection, customFieldMask=customFieldMask, maxResults=GC.Values[GC.USER_MAX_RESULTS])
      while feed:
        _printUser(feed.popleft())
    except GAPI.domainNotFound:
      entityActionFailedWarning([Ent.USER, None, Ent.DOMAIN, domain], Msg.NOT_FOUND)
      return
    except GAPI.invalidInput as e:
      if query and not customFieldMask:
        entityActionFailedWarning([Ent.USER, None], invalidQuery(query))
      elif customFieldMask and not query:
        entityActionFailedWarning([Ent.USER, None], invalidUserSchema(customFieldMask))
      elif query and customFieldMask:
        entityActionFailedWarning([Ent.USER, None], '{0} or {1}'.format(invalidQuery(query), invalidUserSchema(customFieldMask)))
      else:
        entityActionFailedWarning([Ent.USER, None], e.message)
      return
    except (GAPI.badRequest, GAPI.resourceNotFound, GAPI.forbidden):
      accessErrorExit(cd)
  else:
    if fieldsList:
      fields = ','.join(set(fieldsList)).replace('.', '/')
      selectLookup = len(fieldsList) > 1
    else:
      fields = None
      selectLookup = True
    if selectLookup:
      jcount = len(entityList)
      svcargs = dict([('userKey', None), ('fields', fields), ('projection', projection), ('customFieldMask', customFieldMask), ('viewType', viewType)]+GM.Globals[GM.EXTRA_ARGS_LIST])
      dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackPrintUser)
      bcount = 0
      j = 0
      for userEntity in entityList:
        j += 1
        svcparms = svcargs.copy()
        svcparms['userKey'] = normalizeEmailAddressOrUID(userEntity)
        dbatch.add(cd.users().get(**svcparms), request_id=batchRequestID('', 0, 0, j, jcount, svcparms['userKey']))
        bcount += 1
        if bcount >= GC.Values[GC.BATCH_SIZE]:
          dbatch.execute()
          dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackPrintUser)
          bcount = 0
      if bcount > 0:
        dbatch.execute()
    else:
      for userEntity in entityList:
        _printUser({'primaryEmail': normalizeEmailAddressOrUID(userEntity)})
  if sortHeaders:
    sortCSVTitles(['primaryEmail',], titles)
  if select and orderBy:
    import operator
    csvRows.sort(key=operator.itemgetter('name.{0}'.format(orderBy)), reverse=sortOrder == 'DESCENDING')
  if getGroupFeed:
    addTitleToCSVfile('Groups', titles)
    i = 0
    count = len(csvRows)
    for user in csvRows:
      i += 1
      userEmail = user['primaryEmail']
      printGettingAllEntityItemsForWhom(Ent.GROUP_MEMBERSHIP, userEmail, i, count)
      groups = callGAPIpages(cd.groups(), 'list', 'groups',
                             userKey=userEmail, fields='nextPageToken,groups(email)')
      user['Groups'] = delimiter.join([groupname['email'] for groupname in groups])
  if getLicenseFeed:
    addTitlesToCSVfile(['Licenses', 'LicensesDisplay'], titles)
    licenses = doPrintLicenses(returnFields='userId,skuId')
    if licenses:
      for user in csvRows:
        u_licenses = licenses.get(user['primaryEmail'].lower())
        if u_licenses:
          user['Licenses'] = delimiter.join(u_licenses)
          user['LicensesDisplay'] = delimiter.join([SKU.skuIdToDisplayName(skuId) for skuId in u_licenses])
  writeCSVfile(csvRows, titles, 'Users', todrive)

