def _getCourseShowArguments(myarg, courseShowProperties):
  if myarg in ['alias', 'aliases']:
    courseShowProperties['aliases'] = True
  elif myarg == 'show':
    courseShowProperties['members'] = getChoice(COURSE_MEMBER_ARGUMENTS)
  elif myarg == 'fields':
    if not courseShowProperties['fields']:
      courseShowProperties['fields'] = ['id',]
    fieldNameList = getString(Cmd.OB_FIELD_NAME_LIST)
    for field in fieldNameList.lower().replace(',', ' ').split():
      if field in ['alias', 'aliases']:
        courseShowProperties['aliases'] = True
      elif field == 'teachers':
        if courseShowProperties['members'] in ['none', 'all']:
          courseShowProperties['members'] = myarg
        elif courseShowProperties['members'] == 'students':
          courseShowProperties['members'] = 'all'
      elif field == 'students':
        if courseShowProperties['members'] in ['none', 'all']:
          courseShowProperties['members'] = myarg
        elif courseShowProperties['members'] == 'teachers':
          courseShowProperties['members'] = 'all'
      elif field in COURSE_ARGUMENT_TO_PROPERTY_MAP:
        if field != 'id':
          courseShowProperties['fields'].append(COURSE_ARGUMENT_TO_PROPERTY_MAP[field])
      else:
        Cmd.Backup()
        invalidChoiceExit(COURSE_ARGUMENT_TO_PROPERTY_MAP)
  elif myarg == 'skipfields':
    fieldNameList = getString(Cmd.OB_FIELD_NAME_LIST)
    for field in fieldNameList.lower().replace(',', ' ').split():
      if field in ['alias', 'aliases']:
        courseShowProperties['aliases'] = False
      elif field == 'teachers':
        if courseShowProperties['members'] == 'all':
          courseShowProperties['members'] = 'students'
        elif courseShowProperties['members'] == field:
          courseShowProperties['members'] = 'none'
      elif field == 'students':
        if courseShowProperties['members'] == 'all':
          courseShowProperties['members'] = 'teachers'
        elif courseShowProperties['members'] == field:
          courseShowProperties['members'] = 'none'
      elif field in COURSE_ARGUMENT_TO_PROPERTY_MAP:
        if field != 'id':
          courseShowProperties['skips'].append(COURSE_ARGUMENT_TO_PROPERTY_MAP[field])
      else:
        Cmd.Backup()
        invalidChoiceExit(COURSE_ARGUMENT_TO_PROPERTY_MAP)
  else:
    unknownArgumentExit()

