def _printShowCalendars(users, csvFormat):

  def _getPermissions(cal, userCalendar):
    if userCalendar['accessRole'] == 'owner':
      try:
        return callGAPIpages(cal.acl(), 'list', 'items',
                             throw_reasons=GAPI.CALENDAR_THROW_REASONS+[GAPI.NOT_FOUND],
                             calendarId=userCalendar['id'], fields='nextPageToken,items(id,role,scope)')
      except (GAPI.serviceNotAvailable, GAPI.authError, GAPI.notACalendarUser, GAPI.notFound):
        pass
    return collections.deque()

  acls = collections.deque()
  primary = showPermissions = False
  if csvFormat:
    todrive = {}
    titles, csvRows = initializeTitlesCSVfile(None)
  kwargs = {}
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if csvFormat and myarg == 'todrive':
      todrive = getTodriveParameters()
    elif myarg in [Cmd.ARG_ACLS, Cmd.ARG_CALENDARACLS, Cmd.ARG_PERMISSIONS]:
      showPermissions = True
    elif myarg == 'allcalendars':
      pass
    elif myarg == 'primary':
      primary = True
    elif not _getCalendarSelectProperty(myarg, kwargs):
      unknownArgumentExit()
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, cal = validateCalendar(user, i, count)
    if not cal:
      continue
    try:
      calendars = callGAPIpages(cal.calendarList(), 'list', 'items',
                                throw_reasons=GAPI.CALENDAR_THROW_REASONS,
                                **kwargs)
      if primary:
        for calendar in calendars:
          if calendar.get('primary', False):
            calendars = collections.deque([calendar])
            break
        else:
          calendars = collections.deque()
      jcount = len(calendars)
      if not csvFormat:
        entityPerformActionNumItems([Ent.USER, user], jcount, Ent.CALENDAR, i, count)
        if jcount == 0:
          continue
        Ind.Increment()
        j = 0
        for calendar in calendars:
          j += 1
          if showPermissions:
            acls = _getPermissions(cal, calendar)
          _showCalendar(calendar, j, jcount, acls)
        Ind.Decrement()
      else:
        for calendar in calendars:
          row = {'primaryEmail': user, 'calendarId': calendar['id']}
          if showPermissions:
            flattenJSON(_getPermissions(cal, calendar), key='permissions', flattened=row)
          calendar.pop('id', None)
          addRowTitlesToCSVfile(flattenJSON(calendar, flattened=row), csvRows, titles)
    except GAPI.notACalendarUser as e:
      entityActionFailedWarning([Ent.USER, user], e.message, i, count)
    except (GAPI.serviceNotAvailable, GAPI.authError):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)
  if csvFormat:
    sortCSVTitles(['primaryEmail', 'calendarId'], titles)
    writeCSVfile(csvRows, titles, 'Calendars', todrive)

