# gam <UserTypeEntity> update calattendees <CalendarEntity> <EventEntity> [anyorganizer] [csv <FileName>] (replace <EmailAddress> <EmailAddress>)* [doit]
def updateCalendarAttendees(users):
  calendarEntity = getCalendarEntity()
  calendarEventEntity = getCalendarEventEntity()
  csv_file = None
  anyOrganizer = doIt = False
  attendee_map = {}
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'csv':
      csv_file = getString(Cmd.OB_FILE_NAME)
    elif myarg == 'replace':
      origAttendee = getEmailAddress(noUid=True)
      attendee_map[origAttendee] = getEmailAddress(noUid=True)
    elif myarg == 'doit':
      doIt = True
    elif myarg == 'anyorganizer':
      anyOrganizer = True
    else:
      unknownArgumentExit()
  if not attendee_map:
    missingChoiceExit(['csv <FileName>', 'replace <EmailAddress> <EmailAddress>'])
  if csv_file:
    f = openFile(csv_file)
    csvFile = csv.reader(f)
    for row in csvFile:
      if len(row) >= 2:
        attendee_map[row[0].lower()] = row[1].lower()
    closeFile(f)
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, cal, calIds, jcount = _validateUserGetCalendarIds(user, i, count, calendarEntity)
    if jcount == 0:
      continue
    Ind.Increment()
    j = 0
    for calId in calIds:
      j += 1
      Act.Set(Act.UPDATE)
      calId, cal, events, kcount = _validateCalendarGetEvents(user, cal, calId, j, jcount, calendarEventEntity, True)
      if kcount == 0:
        continue
      Ind.Increment()
      k = 0
      for event in events:
        k += 1
        Act.Set(Act.REPLACE)
        if event['status'] == 'cancelled':
          continue
        event_summary = convertUTF8(event.get('summary', event['id']))
        if not anyOrganizer and not event.get('organizer', {}).get('self'):
          continue
        needs_update = False
        for attendee in event.get('attendees', []):
          if 'email' in attendee:
            old_email = attendee['email'].lower()
            new_email = attendee_map.get(old_email)
            if new_email:
              entityModifierNewValueActionPerformed([Ent.EVENT, event_summary, Ent.ATTENDEE, old_email], Act.MODIFIER_WITH, new_email, k, kcount)
              event['attendees'].remove(attendee)
              event['attendees'].append({'email': new_email})
              needs_update = True
        if needs_update:
          Act.Set(Act.UPDATE)
          if doIt:
            try:
              callGAPI(cal.events(), 'patch',
                       throw_reasons=GAPI.CALENDAR_THROW_REASONS+[GAPI.NOT_FOUND, GAPI.FORBIDDEN],
                       calendarId=calId, eventId=event['id'], sendNotifications=False, body={'attendees': event['attendees']}, fields='')
              entityActionPerformed([Ent.EVENT, event_summary], j, jcount)
            except GAPI.notFound as e:
              if not checkCalendarExists(cal, calId):
                entityUnknownWarning(Ent.CALENDAR, calId, j, jcount)
                break
              else:
                entityActionFailedWarning([Ent.CALENDAR, calId, Ent.EVENT, event_summary], e.message, k, kcount)
            except (GAPI.notACalendarUser, GAPI.forbidden) as e:
              entityActionFailedWarning([Ent.CALENDAR, calId], e.message, j, jcount)
              break
            except (GAPI.serviceNotAvailable, GAPI.authError):
              entityServiceNotApplicableWarning(Ent.CALENDAR, calId, j, jcount)
              break
          else:
            entityActionNotPerformedWarning([Ent.EVENT, event_summary], Msg.USE_DOIT_ARGUMENT_TO_PERFORM_ACTION, j, jcount)
      Ind.Decrement()
    Ind.Decrement()

