def doDriveSearch(drive, user, i, count, query=None):
  if GC.Values[GC.SHOW_GETTINGS]:
    printGettingAllEntityItemsForWhom(Ent.DRIVE_FILE_OR_FOLDER, user, i, count, qualifier=queryQualifier(query))
  page_message = getPageMessageForWhom(noNL=True)
  try:
    files = callGAPIpages(drive.files(), 'list', API.DRIVE_FILES_LIST,
                          page_message=page_message,
                          throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.INVALID_QUERY, GAPI.FILE_NOT_FOUND],
                          q=query, fields='nextPageToken,{0}(id)'.format(API.DRIVE_FILES_LIST), maxResults=GC.Values[GC.DRIVE_MAX_RESULTS])
    return [f_file['id'] for f_file in files]
  except GAPI.invalidQuery:
    entityActionFailedWarning([Ent.USER, user, Ent.DRIVE_FILE, None], invalidQuery(query), i, count)
  except GAPI.fileNotFound:
    printGettingEntityItemsForWhomDoneInfo(0)
  except (GAPI.serviceNotAvailable, GAPI.authError):
    entityServiceNotApplicableWarning(Ent.USER, user, i, count)
  return None

def cleanFileIDsList(fileIdEntity, fileIds):
  fileIdEntity['list'] = []
  fileIdEntity['root'] = []
  i = 0
  for fileId in fileIds:
    if fileId[:8].lower() == 'https://' or fileId[:7].lower() == 'http://':
      loc = fileId.find('/d/')
      if loc > 0:
        fileId = fileId[loc+3:]
        loc = fileId.find('/')
        if loc != -1:
          fileId = fileId[:loc]
      else:
        loc = fileId.find('/folderview?id=')
        if loc > 0:
          fileId = fileId[loc+15:]
          loc = fileId.find('&')
          if loc != -1:
            fileId = fileId[:loc]
        else:
          continue
    elif fileId.lower() == 'root':
      fileIdEntity['root'].append(i)
      fileId = fileId.lower()
    fileIdEntity['list'].append(fileId)
    i += 1

def initDriveFileEntity():
  return {'list': [], 'query': None, 'dict': None, 'root': []}

def getDriveFileEntity(orphansOK=False):
  fileIdEntity = initDriveFileEntity()
  entitySelector = getEntitySelector()
  if entitySelector:
    entityList = getEntitySelection(entitySelector, False)
    if isinstance(entityList, dict):
      fileIdEntity['dict'] = entityList
    else:
      cleanFileIDsList(fileIdEntity, entityList)
  else:
    myarg = getString(Cmd.OB_DRIVE_FILE_ID, checkBlank=True)
    mycmd = myarg.lower()
    if mycmd == 'id':
      cleanFileIDsList(fileIdEntity, getStringReturnInList(Cmd.OB_DRIVE_FILE_ID))
    elif mycmd[:3] == 'id:':
      cleanFileIDsList(fileIdEntity, [myarg[3:]])
    elif mycmd == 'ids':
      cleanFileIDsList(fileIdEntity, getString(Cmd.OB_DRIVE_FILE_ID).replace(',', ' ').split())
    elif mycmd[:4] == 'ids:':
      cleanFileIDsList(fileIdEntity, myarg[4:].replace(',', ' ').split())
    elif mycmd == 'query':
      fileIdEntity['query'] = getString(Cmd.OB_QUERY)
    elif mycmd[:6] == 'query:':
      fileIdEntity['query'] = myarg[6:]
    elif mycmd == 'drivefilename':
      fileIdEntity['query'] = ME_IN_OWNERS_AND+"{0} = '{1}'".format(API.DRIVE_FILE_NAME, getString(Cmd.OB_DRIVE_FILE_NAME))
    elif mycmd[:14] == 'drivefilename:':
      fileIdEntity['query'] = ME_IN_OWNERS_AND+"{0} = '{1}'".format(API.DRIVE_FILE_NAME, myarg[14:])
    elif mycmd == 'anydrivefilename':
      fileIdEntity['query'] = "{0} = '{1}'".format(API.DRIVE_FILE_NAME, getString(Cmd.OB_DRIVE_FILE_NAME))
    elif mycmd[:17] == 'anydrivefilename:':
      fileIdEntity['query'] = "{0} = '{1}'".format(API.DRIVE_FILE_NAME, myarg[17:])
    elif mycmd == 'root':
      cleanFileIDsList(fileIdEntity, [mycmd,])
    elif orphansOK and mycmd == 'orphans':
      cleanFileIDsList(fileIdEntity, [mycmd,])
    else:
      cleanFileIDsList(fileIdEntity, [myarg,])
  return fileIdEntity

def _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, drive=None, entityType=None):
  if fileIdEntity['dict']:
    cleanFileIDsList(fileIdEntity, fileIdEntity['dict'][user])
  if not drive:
    user, drive = buildGAPIServiceObject(API.DRIVE, user)
    if not drive:
      return (user, None, 0)
  else:
    user = convertUserUIDtoEmailAddress(user)
  if parameters[DFA_PARENTQUERY]:
    more_parents = doDriveSearch(drive, user, i, count, query=parameters[DFA_PARENTQUERY])
    if more_parents is None:
      setSysExitRC(NO_ENTITIES_FOUND)
      return (user, None, 0)
    body.setdefault('parents', [])
    for a_parent in more_parents:
      body['parents'].append({'id': a_parent})
  if fileIdEntity['query']:
    fileIdEntity['list'] = doDriveSearch(drive, user, i, count, query=fileIdEntity['query'])
    if fileIdEntity['list'] is None:
      setSysExitRC(NO_ENTITIES_FOUND)
      return (user, None, 0)
  elif fileIdEntity['root']:
    try:
      rootFolderId = callGAPI(drive.files(), 'get',
                              throw_reasons=GAPI.DRIVE_THROW_REASONS,
                              fileId='root', fields='id')['id']
    except (GAPI.serviceNotAvailable, GAPI.authError):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)
      return (user, None, 0)
    for j in fileIdEntity['root']:
      fileIdEntity['list'][j] = rootFolderId
  l = len(fileIdEntity['list'])
  if l == 0:
    setSysExitRC(NO_ENTITIES_FOUND)
  if entityType:
    entityPerformActionNumItems([Ent.USER, user], l, entityType, i, count)
  return (user, drive, l)

