def initFilePathInfo():
  return {'ids': {}, 'allPaths': {}, 'localPaths': None}

def getFilePaths(drive, fileTree, initialResult, filePathInfo):
  def _followParent(paths, parentId):
    paths.setdefault(parentId, {})
    if fileTree:
      parentEntry = fileTree.get(parentId)
      if parentEntry:
        if parentEntry['info'][API.DRIVE_FILE_NAME] == parentEntry['info']['id']:
          try:
            result = callGAPI(drive.files(), 'get',
                              throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND],
                              fileId=parentId, fields='{0},{1}'.format(API.DRIVE_FILE_NAME, API.DRIVE_PARENTS_ID))
            parentEntry['info'][API.DRIVE_FILE_NAME] = result[API.DRIVE_FILE_NAME]
            parentEntry['info']['parents'] = result.get('parents', [])
          except (GAPI.fileNotFound, GAPI.serviceNotAvailable, GAPI.authError):
            pass
        filePathInfo['ids'][parentId] = parentEntry['info'][API.DRIVE_FILE_NAME]
        parents = parentEntry['info'].get('parents', [])
      else:
        return
    else:
      try:
        result = callGAPI(drive.files(), 'get',
                          throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND],
                          fileId=parentId, fields='{0},{1}'.format(API.DRIVE_FILE_NAME, API.DRIVE_PARENTS_ID))
        filePathInfo['ids'][parentId] = result[API.DRIVE_FILE_NAME]
        parents = result.get('parents', [])
      except (GAPI.fileNotFound, GAPI.serviceNotAvailable, GAPI.authError):
        return
    if parents:
      for lparent in parents:
        lparentId = lparent['id']
        if lparentId not in filePathInfo['allPaths']:
          _followParent(paths[parentId], lparentId)
          filePathInfo['allPaths'][lparentId] = paths[parentId][lparentId]
        else:
          paths[parentId][lparentId] = filePathInfo['allPaths'][lparentId]

  def _makeFilePaths(localPaths, fplist, filePaths, name):
    for k, v in list(localPaths.items()):
      fplist.append(filePathInfo['ids'].get(k, ''))
      if not v:
        fp = fplist[:]
        fp.reverse()
        fp.append(name)
        filePaths.append(os.path.join(*fp))
        fplist.pop()
        return
      _makeFilePaths(v, fplist, filePaths, name)
      fplist.pop()
    return

  filePaths = []
  parents = initialResult.get('parents', [])
  if parents:
    filePathInfo['localPaths'] = {}
    for parent in parents:
      parentId = parent['id']
      if parentId not in filePathInfo['allPaths']:
        _followParent(filePathInfo['allPaths'], parentId)
      filePathInfo['localPaths'][parentId] = filePathInfo['allPaths'][parentId]
    fplist = []
    _makeFilePaths(filePathInfo['localPaths'], fplist, filePaths, initialResult[API.DRIVE_FILE_NAME])
  return ([Ent.DRIVE_FOLDER, Ent.DRIVE_FILE][initialResult['mimeType'] != MIMETYPE_GA_FOLDER], filePaths)

