def _stripMeInOwners(query):
  if not query:
    return query
  query = query.replace(ME_IN_OWNERS_AND, '')
  query = query.replace(AND_ME_IN_OWNERS, '')
  return query.replace(ME_IN_OWNERS, '').strip() or None

def _stripNotMeInOwners(query):
  if not query:
    return query
  query = query.replace(NOT_ME_IN_OWNERS_AND, '')
  query = query.replace(AND_NOT_ME_IN_OWNERS, '')
  return query.replace(NOT_ME_IN_OWNERS, '').strip() or None

def _convertReaderToCommenter(permission):
  if permission['role'] == 'reader' and permission.get('additionalRoles') and permission['additionalRoles'][0] == 'commenter':
    permission['role'] = permission['additionalRoles'].pop(0)
    if not permission['additionalRoles']:
      del permission['additionalRoles']

def _setRoleConvertCommenterToReader(body, role):
  body['role'] = role
  if role == 'commenter':
    body['role'] = 'reader'
    body['additionalRoles'] = [role]

OWNED_BY_ME_FIELDS_TITLES = ['ownedByMe',]

def buildFileTree(feed, drive):
  fileTree = {'orphans': {'info': {'id': 'orphans', API.DRIVE_FILE_NAME: 'orphans', 'mimeType': MIMETYPE_GA_FOLDER, 'ownedByMe': True}, 'children': []}}
  try:
    f_file = callGAPI(drive.files(), 'get',
                      throw_reasons=GAPI.DRIVE_THROW_REASONS,
                      fileId='root', fields=','.join(FILEPATH_FIELDS+OWNED_BY_ME_FIELDS_TITLES))
    fileTree[f_file['id']] = {'info': f_file, 'children': []}
  except (GAPI.serviceNotAvailable, GAPI.authError):
    pass
  for f_file in feed:
    fileId = f_file['id']
    if fileId not in fileTree:
      fileTree[fileId] = {'info': f_file, 'children': []}
    else:
      fileTree[fileId]['info'] = f_file
    parents = f_file.get('parents', [])
    if not parents:
      parents = [{'id': 'orphans'}]
    for parent in parents:
      parentId = parent['id']
      if parentId not in fileTree:
        fileTree[parentId] = {'info': {'id': parentId, API.DRIVE_FILE_NAME: parentId, 'mimeType': MIMETYPE_GA_FOLDER}, 'children': []}
      fileTree[parentId]['children'].append(fileId)
  return fileTree

def addFilePathsToRow(drive, fileTree, fileEntryInfo, filePathInfo, row, titles):
  _, paths = getFilePaths(drive, fileTree, fileEntryInfo, filePathInfo)
  kcount = len(paths)
  row['paths'] = kcount
  k = 0
  for path in paths:
    key = 'path.{0}'.format(k)
    if key not in titles['set']:
      addTitleToCSVfile(key, titles)
    row[key] = path
    k += 1

