def printDriveFileList(users):
  def _setSelectionFields():
    if fileIdEntity:
      skipObjects.extend([field for field in FILELIST_TITLES if field not in fieldsList])
      fieldsList.extend(FILELIST_FIELDS)
    if filepath:
      skipObjects.extend([field for field in FILEPATH_TITLES if field not in fieldsList])
      fieldsList.extend(FILEPATH_FIELDS)
    if showOwnedBy is not None:
      skipObjects.extend([field for field in OWNED_BY_ME_FIELDS_TITLES if field not in fieldsList])
      fieldsList.extend(OWNED_BY_ME_FIELDS_TITLES)

  def _printFileInfo(drive, f_file):
    if f_file.get('prnt'):
      return
    if showOwnedBy is not None and f_file['ownedByMe'] != showOwnedBy:
      return
    f_file['prnt'] = True
    row = {'Owner': user}
    if filepath:
      addFilePathsToRow(drive, fileTree, f_file, filePathInfo, row, titles)
    for permission in f_file.get('permissions', []):
      _convertReaderToCommenter(permission)
    for attrib in f_file:
      if attrib in skipObjects:
        continue
      if not isinstance(f_file[attrib], dict):
        if isinstance(f_file[attrib], list):
          if f_file[attrib]:
            if attrib not in titles['set']:
              addTitleToCSVfile(attrib, titles)
            if isinstance(f_file[attrib][0], (str, int, bool)):
              row[attrib] = delimiter.join(f_file[attrib])
            else:
              row[attrib] = len(f_file[attrib])
              for j, l_attrib in enumerate(f_file[attrib]):
                for list_attrib in l_attrib:
                  if list_attrib in ['kind', 'etag', 'selfLink']:
                    continue
                  x_attrib = '{0}.{1}.{2}'.format(attrib, j, list_attrib)
                  row[x_attrib] = l_attrib[list_attrib]
                  if x_attrib not in titles['set']:
                    addTitleToCSVfile(x_attrib, titles)
        elif isinstance(f_file[attrib], (str, int, bool)):
          if attrib not in DRIVEFILE_TIME_OBJECTS:
            row[attrib] = f_file[attrib]
          else:
            row[attrib] = formatLocalTime(f_file[attrib])
          if attrib not in titles['set']:
            addTitleToCSVfile(attrib, titles)
        else:
          writeStderr('{0}: {1}, Attribute: {2}, Unknown type: {3}\n'.format(Ent.Singular(Ent.DRIVE_FILE_ID), f_file['id'], attrib, type(f_file[attrib])))
      elif attrib == 'labels':
        for dict_attrib in f_file[attrib]:
          row[dict_attrib] = f_file[attrib][dict_attrib]
          if dict_attrib not in titles['set']:
            addTitleToCSVfile(dict_attrib, titles)
      else:
        for dict_attrib in f_file[attrib]:
          if dict_attrib in ['kind', 'etag']:
            continue
          x_attrib = '{0}.{1}'.format(attrib, dict_attrib)
          row[x_attrib] = f_file[attrib][dict_attrib]
          if x_attrib not in titles['set']:
            addTitleToCSVfile(x_attrib, titles)
    csvRows.append(row)

  def _printDriveFolderContents(drive, fileEntry, depth):
    for childId in fileEntry['children']:
      childEntry = fileTree.get(childId)
      if childEntry:
        _printFileInfo(drive, childEntry['info'])
        if maxdepth == -1 or depth < maxdepth:
          _printDriveFolderContents(drive, childEntry, depth+1)

  allfields = filepath = False
  todrive = {}
  maxdepth = -1
  fieldsList = []
  fieldsTitles = {}
  labelsList = []
  orderByList = []
  skipObjects = ['prnt',]
  titles, csvRows = initializeTitlesCSVfile(['Owner',])
  query = ME_IN_OWNERS
  fileIdEntity = showOwnedBy = fileTree = None
  body, parameters = initializeDriveFileAttributes()
  delimiter = GC.Values[GC.CSV_OUTPUT_FIELD_DELIMITER]
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
