# gam <UserTypeEntity> get drivefile <DriveFileEntity> [format <FileFormatList>] [targetfolder <FilePath>] [revision <Number>]
def getDriveFile(users):
  fileIdEntity = getDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  revisionId = None
  exportFormatName = 'openoffice'
  exportFormats = DOCUMENT_FORMATS_MAP[exportFormatName]
  targetFolder = GC.Values[GC.DRIVE_DIR]
  nocache = False
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'format':
      exportFormatChoices = getString(Cmd.OB_FORMAT_LIST).replace(',', ' ').lower().split()
      exportFormats = []
      for exportFormat in exportFormatChoices:
        if exportFormat in DOCUMENT_FORMATS_MAP:
          exportFormats.extend(DOCUMENT_FORMATS_MAP[exportFormat])
        else:
          Cmd.Backup()
          invalidChoiceExit(DOCUMENT_FORMATS_MAP)
    elif myarg == 'targetfolder':
      targetFolder = os.path.expanduser(getString(Cmd.OB_FILE_PATH))
      if not os.path.isdir(targetFolder):
        os.makedirs(targetFolder)
    elif myarg == 'revision':
      revisionId = getInteger(minVal=1)
    elif myarg == 'nocache':
      nocache = True
    else:
      unknownArgumentExit()
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, entityType=Ent.DRIVE_FILE)
    if jcount == 0:
      continue
    Ind.Increment()
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      extension = None
      try:
        result = callGAPI(drive.files(), 'get',
                          throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND],
                          fileId=fileId, fields='{0},mimeType,{1},downloadUrl,exportLinks'.format(API.DRIVE_FILE_NAME, API.DRIVE_FILE_SIZE))
        if result['mimeType'] == MIMETYPE_GA_FOLDER:
          entityActionNotPerformedWarning([Ent.USER, user, Ent.DRIVE_FOLDER, result[API.DRIVE_FILE_NAME]], Msg.CAN_NOT_BE_DOWNLOADED, j, jcount)
          continue
        if API.DRIVE_FILE_SIZE in result:
          my_line = ['Size', formatFileSize(int(result[API.DRIVE_FILE_SIZE]))]
        else:
          my_line = ['Type', 'Google Doc']
        if 'downloadUrl' in result:
          download_url = result['downloadUrl']
        elif 'exportLinks' in result:
          for exportFormat in exportFormats:
            if exportFormat['mime'] in result['exportLinks']:
              download_url = result['exportLinks'][exportFormat['mime']]
              extension = exportFormat['ext']
              break
          else:
            entityActionNotPerformedWarning([Ent.USER, user, Ent.DRIVE_FILE, result[API.DRIVE_FILE_NAME]],
                                            Msg.FORMAT_NOT_AVAILABLE.format(','.join(exportFormatChoices)), j, jcount)
            continue
        else:
          entityActionNotPerformedWarning([Ent.USER, user, Ent.DRIVE_FILE, result[API.DRIVE_FILE_NAME]],
                                          Msg.FORMAT_NOT_DOWNLOADABLE, j, jcount)
          continue
        safe_file_title = cleanFilename(result[API.DRIVE_FILE_NAME])
        filename = os.path.join(targetFolder, safe_file_title)
        y = 0
        while True:
          if extension and filename.lower()[-len(extension):] != extension:
            filename += extension
          if not os.path.isfile(filename):
            break
          y += 1
          filename = os.path.join(targetFolder, '({0})-{1}'.format(y, safe_file_title))
        if revisionId:
          download_url = '{0}&revision={1}'.format(download_url, revisionId)
        if nocache:
          drive._http.cache = None
        _, content = drive._http.request(download_url)
        status, e = writeFileReturnError(filename, content)
        if status:
          entityModifierNewValueKeyValueActionPerformed([Ent.USER, user, Ent.DRIVE_FILE, result[API.DRIVE_FILE_NAME]], Act.MODIFIER_TO, filename, my_line[0], my_line[1], j, jcount)
        else:
          entityModifierNewValueActionFailedWarning([Ent.USER, user, Ent.DRIVE_FILE, result[API.DRIVE_FILE_NAME]], Act.MODIFIER_TO, filename, e.strerror, j, jcount)
      except GAPI.fileNotFound:
        entityActionFailedWarning([Ent.USER, user, Ent.DRIVE_FILE_OR_FOLDER_ID, fileId], Msg.DOES_NOT_EXIST, j, jcount)
      except (GAPI.serviceNotAvailable, GAPI.authError):
        entityServiceNotApplicableWarning(Ent.USER, user, i, count)
        break
    Ind.Decrement()

# gam <UserTypeEntity> transfer drive <UserItem> [keepuser] [retainrole reader|commenter|writer|owner|editor] (orderby <DriveOrderByFieldName> [ascending|descending])*
def transferDriveFiles(users):

  def _newParents(oldParents, rootId):
    parents = []
    for parent in oldParents:
      parentId = parent['id']
      if parentId == rootId:
        parents.append({'id': targetParentId})
      else:
        parents.append({'id': parentIdMap.get(parentId, parentId)})
    if not parents:
      parents.append({'id': targetParentId})
    return parents

  def _transferDriveFiles(fileEntry, rootId):
    jcount = len(fileEntry['children'])
    if jcount == 0:
      return
    j = 0
    for childId in fileEntry['children']:
      j += 1
      childEntry = fileTree.get(childId)
      if not childEntry or childEntry.get('xfer'):
        continue
      childEntry['xfer'] = True
      childFileName = childEntry['info'][API.DRIVE_FILE_NAME]
      childFileType = [Ent.DRIVE_FOLDER, Ent.DRIVE_FILE][childEntry['info']['mimeType'] != MIMETYPE_GA_FOLDER]
      if childEntry['info']['ownedByMe']:
        try:
          Act.Set(Act.TRANSFER_OWNERSHIP)
          callGAPI(sourceDrive.permissions(), API.DRIVE_CREATE_PERMISSIONS,
                   throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.INVALID_SHARING_REQUEST, GAPI.FORBIDDEN],
                   fileId=childId, sendNotificationEmails=False, body=targetPermissionsBody, fields='')
          entityModifierNewValueItemValueActionPerformed([Ent.USER, sourceUser, childFileType, childFileName], Act.MODIFIER_TO, None, Ent.USER, targetUser, j, jcount)
          callGAPI(targetDrive.files(), API.DRIVE_PATCH_FILE,
                   throw_reasons=GAPI.DRIVE_THROW_REASONS, retry_reasons=[GAPI.FILE_NOT_FOUND],
                   fileId=childId, body={'parents': _newParents(childEntry['info']['parents'], rootId)}, fields='')
          if retainSourceRoleBody:
            if retainSourceRoleBody['role'] != 'writer':
              Act.Set(Act.UPDATE)
              callGAPI(targetDrive.permissions(), API.DRIVE_PATCH_PERMISSIONS,
                       throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.PERMISSION_NOT_FOUND, GAPI.BAD_REQUEST, GAPI.FORBIDDEN],
                       fileId=childId, permissionId=sourcePermissionId, body=retainSourceRoleBody)
            entityModifierNewValueActionPerformed([Ent.USER, sourceUser, childFileType, childFileName, Ent.ROLE, 'owner'], Act.MODIFIER_TO, retainSourceRoleBody['role'], j, jcount)
          else:
            Act.Set(Act.DELETE)
            callGAPI(targetDrive.permissions(), 'delete',
                     throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.PERMISSION_NOT_FOUND, GAPI.BAD_REQUEST, GAPI.FORBIDDEN],
                     fileId=childId, permissionId=sourcePermissionId)
            entityActionPerformed([Ent.USER, sourceUser, childFileType, childFileName, Ent.ROLE, 'owner'], j, jcount)
        except (GAPI.fileNotFound, GAPI.forbidden, GAPI.internalError, GAPI.badRequest) as e:
          entityActionFailedWarning([Ent.USER, sourceUser, childFileType, childFileName], e.message, j, jcount)
        except GAPI.permissionNotFound:
          entityDoesNotHaveItemWarning([Ent.USER, sourceUser, childFileType, childFileName, Ent.PERMISSION_ID, sourcePermissionId], j, jcount)
        except (GAPI.invalidSharingRequest) as e:
          entityActionFailedWarning([Ent.USER, sourceUser, childFileType, childFileName], Ent.EntityTypeNameMessage(Ent.PERMISSION_ID, sourcePermissionId, e.message), j, jcount)
        except (GAPI.serviceNotAvailable, GAPI.authError):
          entityServiceNotApplicableWarning(Ent.USER, sourceUser, 0, 0)
      else:
        if childFileType == Ent.DRIVE_FILE:
          continue
### Skip if no children??
        try:
          result = callGAPIpages(targetDrive.files(), 'list', API.DRIVE_FILES_LIST,
                                 throw_reasons=GAPI.DRIVE_THROW_REASONS,
                                 q=ME_IN_OWNERS_AND+"mimeType = '{0}' and {1} = '{2}'".format(MIMETYPE_GA_FOLDER, API.DRIVE_FILE_NAME, childFileName),
                                 fields='nextPageToken,{0}(id)'.format(API.DRIVE_FILES_LIST))
          if len(result) > 0:
            parentIdMap[childId] = result[0]['id']
          else:
            parentIdMap[childId] = callGAPI(targetDrive.files(), API.DRIVE_CREATE_FILE,
                                            throw_reasons=GAPI.DRIVE_THROW_REASONS,
                                            body={'parents': _newParents(childEntry['info']['parents'], rootId), API.DRIVE_FILE_NAME: childFileName, 'mimeType': MIMETYPE_GA_FOLDER}, fields='id')['id']
        except (GAPI.serviceNotAvailable, GAPI.authError):
          entityServiceNotApplicableWarning(Ent.USER, sourceUser, 0, 0)
      if childEntry['info']['mimeType'] == MIMETYPE_GA_FOLDER:
        Ind.Increment()
        _transferDriveFiles(childEntry, rootId)
        Ind.Decrement()

  targetUser = getEmailAddress()
  orderByList = []
  retainSourceRoleBody = {}
  targetFolderId = targetFolderName = None
  targetUserFolderPattern = '#user# old files'
  parentIdMap = {}
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'keepuser':
      retainSourceRoleBody['body'] = 'writer'
    elif myarg == 'retainrole':
      retainSourceRole = getChoice(DRIVEFILE_ACL_ROLES_MAP, mapChoice=True)
      if retainSourceRole == 'owner':
        retainSourceRole = 'writer'
      _setRoleConvertCommenterToReader(retainSourceRoleBody, retainSourceRole)
    elif myarg == 'orderby':
      fieldName = getChoice(DRIVEFILE_ORDERBY_CHOICES_MAP, mapChoice=True)
      if getChoice(SORTORDER_CHOICES_MAP, defaultChoice=None, mapChoice=True) != 'DESCENDING':
        orderByList.append(fieldName)
      else:
        orderByList.append('{0} desc'.format(fieldName))
    elif myarg == 'targetfolderid':
      targetFolderIdLocation = Cmd.Location()
      targetFolderId = getString(Cmd.OB_DRIVE_FILE_ID)
    elif myarg == 'targetfoldername':
      targetFolderNameLocation = Cmd.Location()
      targetFolderName = getString(Cmd.OB_DRIVE_FILE_NAME)
    elif myarg == 'targetuserfoldername':
      targetUserFolderPattern = getString(Cmd.OB_DRIVE_FILE_NAME)
    else:
      unknownArgumentExit()
  orderBy = ','.join(orderByList) if orderByList else None
  targetUser, targetDrive = buildGAPIServiceObject(API.DRIVE, targetUser)
  if not targetDrive:
    return
  try:
    result = callGAPI(targetDrive.about(), 'get',
                      throw_reasons=GAPI.DRIVE_THROW_REASONS,
                      fields='quotaBytesTotal,quotaBytesUsed')
    targetDriveFree = int(result['quotaBytesTotal']) - int(result['quotaBytesUsed'])
    if not targetFolderId and not targetFolderName:
      result = callGAPI(targetDrive.files(), 'get',
                        throw_reasons=GAPI.DRIVE_THROW_REASONS,
                        fileId='root', fields='id,{0}'.format(API.DRIVE_FILE_NAME))
      targetFolderId = result['id']
      targetFolderName = result[API.DRIVE_FILE_NAME]
    else:
      if targetFolderId:
        targetFolder = callGAPI(targetDrive.files(), 'get',
                                throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND],
                                fileId=targetFolderId, fields='id,{0},ownedByMe,mimeType'.format(API.DRIVE_FILE_NAME))
        if targetFolder['mimeType'] != MIMETYPE_GA_FOLDER:
          Cmd.SetLocation(targetFolderIdLocation)
          usageErrorExit(formatKeyValueList(Ind.Spaces(),
                                            [Ent.Singular(Ent.USER), targetUser,
                                             Ent.Singular(Ent.DRIVE_FOLDER_ID), targetFolderId,
                                             Msg.NOT_AN_ENTITY.format(Ent.Singular(Ent.DRIVE_FOLDER))],
                                            '\n'))
        if not targetFolder['ownedByMe']:
          Cmd.SetLocation(targetFolderIdLocation)
          usageErrorExit(formatKeyValueList(Ind.Spaces(),
                                            [Ent.Singular(Ent.USER), targetUser,
                                             Ent.Singular(Ent.DRIVE_FOLDER_ID), targetFolderId,
                                             Msg.NOT_OWNED_BY.format(targetUser)],
                                            '\n'))
        targetFolderName = targetFolder[API.DRIVE_FILE_NAME]
      elif targetFolderName:
        result = callGAPIpages(targetDrive.files(), 'list', API.DRIVE_FILES_LIST,
                               throw_reasons=GAPI.DRIVE_THROW_REASONS,
                               q=ME_IN_OWNERS_AND+"mimeType = '{0}' and {1} = '{2}'".format(MIMETYPE_GA_FOLDER, API.DRIVE_FILE_NAME, targetFolderName),
                               fields='nextPageToken,{0}(id)'.format(API.DRIVE_FILES_LIST))
        if len(result) == 0:
          Cmd.SetLocation(targetFolderNameLocation)
          usageErrorExit(formatKeyValueList(Ind.Spaces(),
                                            [Ent.Singular(Ent.USER), targetUser,
                                             Ent.Singular(Ent.DRIVE_FOLDER), targetFolderName,
                                             Msg.DOES_NOT_EXIST],
                                            '\n'))
        targetFolderId = result[0]['id']
  except GAPI.fileNotFound:
    Cmd.SetLocation(targetFolderIdLocation)
    usageErrorExit(formatKeyValueList(Ind.Spaces(),
                                      [Ent.Singular(Ent.USER), targetUser,
                                       Ent.Singular(Ent.DRIVE_FOLDER_ID), targetFolderId,
                                       Msg.DOES_NOT_EXIST],
                                      '\n'))
  except (GAPI.serviceNotAvailable, GAPI.authError):
    entityServiceNotApplicableWarning(Ent.TARGET_USER, targetUser)
    return
  targetPermissionsBody = {'role': 'owner', 'type': 'user', API.DRIVE_PERMISSIONS_GROUP_USER_TYPE_VALUE: targetUser}
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    sourceUser, sourceDrive = buildGAPIServiceObject(API.DRIVE, user)
    if not sourceDrive:
      continue
    sourceUserName, _ = splitEmailAddress(sourceUser)
    try:
      result = callGAPI(sourceDrive.about(), 'get',
                        throw_reasons=GAPI.DRIVE_THROW_REASONS,
                        fields='quotaBytesTotal,quotaBytesUsed,permissionId,rootFolderId')
      sourceDriveSize = int(result['quotaBytesUsed'])
      sourcePermissionId = result['permissionId']
      sourceRoot = result['rootFolderId']
      if targetDriveFree < sourceDriveSize:
        printWarningMessage(TARGET_DRIVE_SPACE_ERROR_RC,
                            '{0} {1}'.format(Msg.NO_TRANSFER_LACK_OF_DISK_SPACE,
                                             formatKeyValueList('',
                                                                ['Source drive size', formatFileSize(sourceDriveSize),
                                                                 'Target drive free', formatFileSize(targetDriveFree)],
                                                                '')))
        continue
      printKeyValueList(['Source drive size', formatFileSize(sourceDriveSize),
                         'Target drive free', formatFileSize(targetDriveFree)])
      targetDriveFree = targetDriveFree - sourceDriveSize # prep targetDriveFree for next user
      printGettingAllEntityItemsForWhom(Ent.DRIVE_FILE_OR_FOLDER, Ent.EntityTypeName(Ent.SOURCE_USER, user), i, count)
      page_message = getPageMessageForWhom()
      sourceDriveFiles = callGAPIpages(sourceDrive.files(), 'list', API.DRIVE_FILES_LIST,
                                       page_message=page_message,
                                       throw_reasons=GAPI.DRIVE_THROW_REASONS,
                                       q="trashed = false", orderBy=orderBy,
                                       fields='nextPageToken,{0}(id,{1},{2},mimeType,ownedByMe)'.format(API.DRIVE_FILES_LIST, API.DRIVE_FILE_NAME, API.DRIVE_PARENTS_ID),
                                       maxResults=GC.Values[GC.DRIVE_MAX_RESULTS])
      targetUserFolderName = targetUserFolderPattern.replace('#user#', sourceUser)
      targetUserFolderName = targetUserFolderName.replace('#email#', sourceUser)
      targetUserFolderName = targetUserFolderName.replace('#username#', sourceUserName)
      result = callGAPIpages(targetDrive.files(), 'list', API.DRIVE_FILES_LIST,
                             throw_reasons=GAPI.DRIVE_THROW_REASONS,
                             q=ME_IN_OWNERS_AND+"mimeType = '{0}' and {1} = '{2}' and '{3}' in parents".format(MIMETYPE_GA_FOLDER, API.DRIVE_FILE_NAME, targetUserFolderName, targetFolderId),
                             fields='nextPageToken,{0}(id)'.format(API.DRIVE_FILES_LIST))
      if len(result) > 0:
        targetParentId = result[0]['id']
      else:
        targetParentId = callGAPI(targetDrive.files(), API.DRIVE_CREATE_FILE,
                                  throw_reasons=GAPI.DRIVE_THROW_REASONS,
                                  body={'parents': [{'id': targetFolderId}], API.DRIVE_FILE_NAME: targetUserFolderName, 'mimeType': MIMETYPE_GA_FOLDER}, fields='id')['id']
      targetUserFolderName = os.path.join(targetFolderName, targetUserFolderName)
      fileTree = buildFileTree(sourceDriveFiles, sourceDrive)
      Ind.Increment()
      _transferDriveFiles(fileTree[sourceRoot], sourceRoot)
      _transferDriveFiles(fileTree['orphans'], 'orphans')
      Ind.Decrement()
    except (GAPI.serviceNotAvailable, GAPI.authError):
      entityServiceNotApplicableWarning(Ent.USER, sourceUser, i, count)

