# gam <UserTypeEntity> claim ownership <DriveFileEntity> [skipids <DriveFileEntity>] [skipusers <UserTypeEntity>] [subdomains <DomainNameEntity>] [includetrashed] [restricted [<Boolean>]] [writerscantshare [<Boolean>]] [preview] [filepath] [todrive [<ToDriveAttributes>]]
def claimDriveFolderOwnership(users):
  def _identifyFilesToClaim(fileEntry, skipids, skipusers, trashed):
    for childId in fileEntry['children']:
      childEntry = fileTree.get(childId)
      if childEntry:
        childEntryInfo = childEntry['info']
        if childId not in skipids and (trashed or not childEntryInfo['labels']['trashed']):
          owner = childEntryInfo['owners'][0]['emailAddress']
          if not childEntryInfo['ownedByMe'] and owner not in skipusers:
            filesToClaim.setdefault(owner, {})
            if childId not in filesToClaim[owner]:
              filesToClaim[owner][childId] = {'name': childEntryInfo[API.DRIVE_FILE_NAME], 'type': [Ent.DRIVE_FILE, Ent.DRIVE_FOLDER][childEntryInfo['mimeType'] == MIMETYPE_GA_FOLDER]}
          if childEntryInfo['mimeType'] == MIMETYPE_GA_FOLDER:
            _identifyFilesToClaim(childEntry, skipids, skipusers, trashed)

  fileIdEntity = getDriveFileEntity()
  skipFileIdEntity = initDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  skipbody, skipparameters = initializeDriveFileAttributes()
  skipusers = []
  subdomains = []
  csvFormat = filepath = trashed = False
  todrive = {}
  fileTree = None
  bodyShare = {}
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'skipids':
      skipFileIdEntity = getDriveFileEntity()
    elif myarg == 'skipusers':
      _, skipusers = getEntityToModify(defaultEntityType=Cmd.ENTITY_USERS)
    elif myarg == 'subdomains':
      subdomains = getEntityList(Cmd.OB_DOMAIN_NAME_ENTITY)
    elif myarg == 'includetrashed':
      trashed = True
    elif myarg == 'restricted':
      bodyShare['labels'][API.DRIVE_FILE_LABEL_RESTRICTED] = getBoolean(defaultValue=True)
    elif myarg == 'writerscantshare':
      bodyShare['writersCanShare'] = not getBoolean(defaultValue=True)
    elif myarg == 'preview':
      csvFormat = True
    elif myarg == 'filepath':
      filepath = True
    elif myarg == 'todrive':
      todrive = getTodriveParameters()
    else:
      unknownArgumentExit()
  Act.Set(Act.CLAIM_OWNERSHIP)
  if csvFormat:
    titles, csvRows = initializeTitlesCSVfile(['NewOwner', 'OldOwner', 'type', 'id', API.DRIVE_FILE_NAME])
    if filepath:
      addTitlesToCSVfile(['paths',], titles)
  else:
    filepath = False
  body = {'role': 'owner'}
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    origUser = user
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters)
    if not drive:
      continue
    permissionId = validateUserGetPermissionId(user)
    if not permissionId:
      continue
    entityPerformActionNumItems([Ent.USER, user], jcount, Ent.DRIVE_FILE_OR_FOLDER, i, count)
    if jcount == 0:
      continue
    if filepath:
      filePathInfo = initFilePathInfo()
    bodyAdd = {'role': 'writer', 'type': 'user', API.DRIVE_PERMISSIONS_GROUP_USER_TYPE_VALUE: user}
    if skipFileIdEntity['query'] or skipFileIdEntity['root']:
      _validateUserGetFileIDs(origUser, i, count, skipFileIdEntity, skipbody, skipparameters, drive=drive)
    try:
      printGettingAllEntityItemsForWhom(Ent.DRIVE_FILE_OR_FOLDER, user, i, count)
      page_message = getPageMessageForWhom()
      feed = callGAPIpages(drive.files(), 'list', API.DRIVE_FILES_LIST,
                           page_message=page_message,
                           throw_reasons=GAPI.DRIVE_THROW_REASONS,
                           fields='nextPageToken,{0}(id,{1},{2},mimeType,ownedByMe,owners(emailAddress),labels(trashed))'.format(API.DRIVE_FILES_LIST, API.DRIVE_FILE_NAME, API.DRIVE_PARENTS_ID),
                           maxResults=GC.Values[GC.DRIVE_MAX_RESULTS])
    except (GAPI.serviceNotAvailable, GAPI.authError):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)
      continue
    fileTree = buildFileTree(feed, drive)
    Ind.Increment()
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      filesToClaim = {}
      fileEntry = fileTree.get(fileId)
      if not fileEntry:
        entityActionFailedWarning([Ent.USER, user, Ent.DRIVE_FILE_OR_FOLDER, fileId], Msg.NOT_FOUND, j, jcount)
        continue
      fileEntryInfo = fileEntry['info']
      entityType = [Ent.DRIVE_FILE, Ent.DRIVE_FOLDER][fileEntryInfo['mimeType'] == MIMETYPE_GA_FOLDER]
      fileDesc = '{0} ({1})'.format(fileEntryInfo[API.DRIVE_FILE_NAME], fileId)
      entityPerformActionItemValue([Ent.USER, user], entityType, fileDesc, j, jcount)
      if not fileEntryInfo['ownedByMe'] and fileId not in skipFileIdEntity['list']:
        owner = fileEntryInfo['owners'][0]['emailAddress']
        if trashed or not fileEntryInfo['labels']['trashed'] and owner not in skipusers:
          filesToClaim.setdefault(owner, {})
          if fileId not in filesToClaim[owner]:
            filesToClaim[owner][fileId] = {'name': fileEntryInfo[API.DRIVE_FILE_NAME], 'type': entityType}
      _identifyFilesToClaim(fileEntry, skipFileIdEntity['list'], skipusers, trashed)
      if csvFormat:
        for oldOwner in filesToClaim:
          for claimFileId, fileInfo in list(filesToClaim[oldOwner].items()):
            row = {'NewOwner': user, 'OldOwner': oldOwner, 'type': Ent.Singular(fileInfo['type']), 'id': claimFileId, API.DRIVE_FILE_NAME: fileInfo['name']}
            if filepath:
              addFilePathsToRow(drive, fileTree, fileTree[claimFileId]['info'], filePathInfo, row, titles)
            csvRows.append(row)
        continue
      Ind.Increment()
      kcount = len(filesToClaim)
      entityPerformActionNumItems([Ent.USER, user], kcount, Ent.USER, i, count)
      Ind.Increment()
      k = 0
      for oldOwner in filesToClaim:
        k += 1
        _, userdomain = splitEmailAddress(oldOwner)
        lcount = len(filesToClaim[oldOwner])
        if userdomain == GC.Values[GC.DOMAIN] or userdomain in subdomains:
          _, source_drive = buildGAPIServiceObject(API.DRIVE, oldOwner)
          entityPerformActionNumItemsModifier([Ent.USER, user], lcount, Ent.DRIVE_FILE_OR_FOLDER, '{0} {1}: {2}'.format(Act.MODIFIER_FROM, Ent.Singular(Ent.USER), oldOwner), k, kcount)
          Ind.Increment()
          l = 0
          for fileId, fileInfo in list(filesToClaim[oldOwner].items()):
            l += 1
            if bodyShare:
              callGAPI(source_drive.files(), 'patch',
                       fileId=fileId, body=bodyShare, fields='')
            entityType = fileInfo['type']
            fileDesc = '{0} ({1})'.format(fileInfo['name'], fileId)
            try:
              callGAPI(source_drive.permissions(), API.DRIVE_PATCH_PERMISSIONS,
                       throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.PERMISSION_NOT_FOUND],
                       fileId=fileId, permissionId=permissionId, transferOwnership=True, body=body, fields='')
              entityModifierNewValueItemValueActionPerformed([Ent.USER, user, entityType, fileDesc], Act.MODIFIER_FROM, None, Ent.USER, oldOwner, l, lcount)
            except GAPI.permissionNotFound:
              # if claimer not in ACL (file might be visible for all with link)
              try:
                Act.Set(Act.ADD)
                callGAPI(source_drive.permissions(), API.DRIVE_CREATE_PERMISSIONS,
                         throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.INVALID_SHARING_REQUEST, GAPI.FILE_NOT_FOUND],
                         fileId=fileId, sendNotificationEmails=False, body=bodyAdd, fields='')
                entityActionPerformed([Ent.USER, user, entityType, fileDesc, Ent.PERMISSION_ID, permissionId], l, lcount)
                Act.Set(Act.CLAIM_OWNERSHIP)
                callGAPI(source_drive.permissions(), API.DRIVE_PATCH_PERMISSIONS,
                         throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.PERMISSION_NOT_FOUND],
                         fileId=fileId, permissionId=permissionId, transferOwnership=True, body=body, fields='')
                entityModifierNewValueItemValueActionPerformed([Ent.USER, user, entityType, fileDesc], Act.MODIFIER_FROM, None, Ent.USER, oldOwner, l, lcount)
              except GAPI.invalidSharingRequest as e:
                entityActionFailedWarning([Ent.USER, user, entityType, fileDesc], Ent.EntityTypeNameMessage(Ent.PERMISSION_ID, permissionId, e.message), l, lcount)
              except GAPI.permissionNotFound:
                entityDoesNotHaveItemWarning([Ent.USER, user, entityType, fileDesc, Ent.PERMISSION_ID, permissionId], l, lcount)
              except GAPI.fileNotFound:
                entityActionFailedWarning([Ent.USER, user, entityType, fileDesc], Msg.DOES_NOT_EXIST, l, lcount)
              except (GAPI.serviceNotAvailable, GAPI.authError):
                entityServiceNotApplicableWarning(Ent.USER, user, i, count)
            except GAPI.fileNotFound:
              entityActionFailedWarning([Ent.USER, user, entityType, fileDesc], Msg.DOES_NOT_EXIST, l, lcount)
            except (GAPI.serviceNotAvailable, GAPI.authError):
              entityServiceNotApplicableWarning(Ent.USER, user, i, count)
              break
          Ind.Decrement()
        else:
          entityPerformActionModifierNumItemsModifier([Ent.USER, user], 'Not Performed', kcount, Ent.DRIVE_FILE_OR_FOLDER, '{0} {1}: {2}'.format(Act.MODIFIER_FROM, Ent.Singular(Ent.USER), oldOwner), j, jcount)
          Ind.Increment()
          l = 0
          for fileId, fileInfo in list(filesToClaim[oldOwner].items()):
            l += 1
            entityActionNotPerformedWarning([Ent.USER, user, fileInfo['type'], '{0} ({1})'.format(fileInfo['name'], fileId)],
                                            Msg.USER_IN_OTHER_DOMAIN.format(Ent.Singular(Ent.USER), oldOwner), l, lcount)
          Ind.Decrement()
      Ind.Decrement()
      Ind.Decrement()
    Ind.Decrement()
  if csvFormat:
    writeCSVfile(csvRows, titles, 'Files to Claim Ownership', todrive)

