DRIVEFILE_ACL_TIME_OBJECTS = [API.DRIVE_PERMISSIONS_EXPIRATION_DATE_TIME]

# DriveFileACL commands utilities
def _showDriveFilePermission(permission):
  if API.DRIVE_PERMISSIONS_NAME in permission:
    printKeyValueList([permission[API.DRIVE_PERMISSIONS_NAME]])
  elif 'id' in permission:
    if permission['id'] == 'anyone':
      printKeyValueList(['Anyone'])
    elif permission['id'] == 'anyoneWithLink':
      printKeyValueList(['Anyone with Link'])
    else:
      printKeyValueList([permission['id']])
  Ind.Increment()
  _convertReaderToCommenter(permission)
  for key in permission:
    if key not in ['kind', 'etag', 'selfLink', API.DRIVE_PERMISSIONS_NAME]:
      if key not in DRIVEFILE_ACL_TIME_OBJECTS:
        if key != 'additionalRoles':
          printKeyValueList([key, permission[key]])
        else:
          printKeyValueList([key, ','.join(permission[key])])
      else:
        printKeyValueList([key, formatLocalTime(permission[key])])
  Ind.Decrement()
#
DRIVEFILE_ACL_ROLES_MAP = {
  'commenter': 'commenter',
  'editor': 'writer',
  'owner': 'owner',
  'read': 'reader',
  'reader': 'reader',
  'writer': 'writer',
  }

DRIVEFILE_ACL_PERMISSION_TYPES = ['anyone', 'domain', 'group', 'user',] # anyone must be first element

# gam <UserTypeEntity> add drivefileacl <DriveFileEntity> anyone|(user <UserItem>)|(group <GroupItem>)|(domain <DomainName>)
#	(role reader|commenter|writer|owner|editor) [withlink|(allowfilediscovery <Boolean>)] [expiration <Time>] [sendmail] [emailmessage <String>] [showtitles]
def addDriveFileACL(users):
  sendNotificationEmails = showTitles = False
  emailMessage = None
  fileIdEntity = getDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  body['type'] = getChoice(DRIVEFILE_ACL_PERMISSION_TYPES)
  if body['type'] != 'anyone':
    if body['type'] != 'domain':
      body[API.DRIVE_PERMISSIONS_GROUP_USER_TYPE_VALUE] = permissionId = getEmailAddress()
    else:
      body[API.DRIVE_PERMISSIONS_DOMAIN_TYPE_VALUE] = permissionId = getString(Cmd.OB_DOMAIN_NAME)
  else:
    permissionId = 'anyone'
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'withlink':
      body['withLink'] = True
    elif myarg == 'allowfilediscovery':
      body['withLink'] = not getBoolean()
    elif myarg == 'role':
      roleLocation = Cmd.Location()
      _setRoleConvertCommenterToReader(body, getChoice(DRIVEFILE_ACL_ROLES_MAP, mapChoice=True))
    elif myarg == 'expiration':
      body[API.DRIVE_PERMISSIONS_EXPIRATION_DATE_TIME] = getFullTime()
    elif myarg == 'sendemail':
      sendNotificationEmails = True
    elif myarg == 'emailmessage':
      sendNotificationEmails = True
      emailMessage = getString(Cmd.OB_STRING)
    elif myarg == 'showtitles':
      showTitles = True
    else:
      unknownArgumentExit()
  if 'role' not in body:
    missingArgumentExit('role {0}'.format(formatChoiceList(DRIVEFILE_ACL_ROLES_MAP)))
  if body['role'] == 'owner' and body['type'] != 'user':
    Cmd.SetLocation(roleLocation)
    usageErrorExit(Msg.INVALID_OWNER_TYPE.format(body['role'], body['type']))
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, entityType=Ent.DRIVE_FILE_OR_FOLDER_ACL)
    if jcount == 0:
      continue
    Ind.Increment()
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      try:
        fileName = fileId
        entityType = Ent.DRIVE_FILE_OR_FOLDER_ID
        if showTitles:
          result = callGAPI(drive.files(), 'get',
                            throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR],
                            fileId=fileId, fields=API.DRIVE_FILE_NAME)
          if result and API.DRIVE_FILE_NAME in result:
            entityType = Ent.DRIVE_FILE_OR_FOLDER
            fileName = result[API.DRIVE_FILE_NAME]+'('+fileId+')'
        permission = callGAPI(drive.permissions(), API.DRIVE_CREATE_PERMISSIONS,
                              throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR, GAPI.INVALID_SHARING_REQUEST],
                              fileId=fileId, sendNotificationEmails=sendNotificationEmails, emailMessage=emailMessage, body=body)
        entityActionPerformed([Ent.USER, user, entityType, fileName, Ent.PERMISSION_ID, permissionId], j, jcount)
        _showDriveFilePermission(permission)
      except (GAPI.fileNotFound, GAPI.forbidden, GAPI.internalError) as e:
        entityActionFailedWarning([Ent.USER, user, entityType, fileName], e.message, j, jcount)
      except (GAPI.invalidSharingRequest) as e:
        entityActionFailedWarning([Ent.USER, user, entityType, fileName], Ent.EntityTypeNameMessage(Ent.PERMISSION_ID, permissionId, e.message), j, jcount)
      except (GAPI.serviceNotAvailable, GAPI.authError):
        entityServiceNotApplicableWarning(Ent.USER, user, i, count)
        break
    Ind.Decrement()

# gam <UserTypeEntity> update drivefileacl <DriveFileEntity> <DriveFilePermissionIDorEmail>
#	(role reader|commenter|writer|owner|editor) [withlink|(allowfilediscovery <Boolean>)] [expiration <Time>] [removeexpiration <Boolean>] [transferownership <Boolean>] [showtitles]
def updateDriveFileACLs(users):
  fileIdEntity = getDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  isEmail, permissionId = getPermissionId()
  removeExpiration = transferOwnership = False
  showTitles = False
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'withlink':
      body['withLink'] = True
    elif myarg == 'allowfilediscovery':
      body['withLink'] = not getBoolean()
    elif myarg == 'role':
      _setRoleConvertCommenterToReader(body, getChoice(DRIVEFILE_ACL_ROLES_MAP, mapChoice=True))
    elif myarg == 'expiration':
      body[API.DRIVE_PERMISSIONS_EXPIRATION_DATE_TIME] = getFullTime()
    elif myarg == 'removeexpiration':
      removeExpiration = getBoolean()
    elif myarg == 'transferownership':
      transferOwnership = getBoolean()
    elif myarg == 'showtitles':
      showTitles = True
    else:
      unknownArgumentExit()
  if removeExpiration is None and 'role' not in body:
    missingArgumentExit('role {0}'.format(formatChoiceList(DRIVEFILE_ACL_ROLES_MAP)))
  if isEmail:
    permissionId = validateUserGetPermissionId(permissionId)
    if not permissionId:
      return
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, entityType=Ent.DRIVE_FILE_OR_FOLDER_ACL)
    if jcount == 0:
      continue
    Ind.Increment()
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      try:
        fileName = fileId
        entityType = Ent.DRIVE_FILE_OR_FOLDER_ID
        if showTitles:
          result = callGAPI(drive.files(), 'get',
                            throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR],
                            fileId=fileId, fields=API.DRIVE_FILE_NAME)
          if result and API.DRIVE_FILE_NAME in result:
            entityType = Ent.DRIVE_FILE_OR_FOLDER
            fileName = result[API.DRIVE_FILE_NAME]+'('+fileId+')'
        permission = callGAPI(drive.permissions(), API.DRIVE_PATCH_PERMISSIONS,
                              throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR,
                                                                      GAPI.BAD_REQUEST, GAPI.INVALID_OWNERSHIP_TRANSFER, GAPI.PERMISSION_NOT_FOUND],
                              fileId=fileId, permissionId=permissionId, removeExpiration=removeExpiration,
                              transferOwnership=transferOwnership, body=body)
        entityActionPerformed([Ent.USER, user, entityType, fileName, Ent.PERMISSION_ID, permissionId], j, jcount)
        _showDriveFilePermission(permission)
      except (GAPI.fileNotFound, GAPI.forbidden, GAPI.internalError, GAPI.badRequest, GAPI.invalidOwnershipTransfer) as e:
        entityActionFailedWarning([Ent.USER, user, entityType, fileName], e.message, j, jcount)
      except GAPI.permissionNotFound:
        entityDoesNotHaveItemWarning([Ent.USER, user, entityType, fileName, Ent.PERMISSION_ID, permissionId], j, jcount)
      except (GAPI.serviceNotAvailable, GAPI.authError):
        entityServiceNotApplicableWarning(Ent.USER, user, i, count)
        break
    Ind.Decrement()

# gam <UserTypeEntity> add permissions <DriveFileEntity> <DriveFilePermissionsEntity> [expiration <Time>] [sendmail] [emailmessage <String>]
def addDriveFilePermissions(users):

  def _makePermissionBody(permission):
    body = {}
    try:
      scope, role = permission.split(';', 1)
      if scope == 'anyone' or scope == 'anyonewithlink':
        body['type'] = body['id'] = 'anyone'
        body['withLink'] = scope == 'anyonewithlink'
      else:
        body['type'], value = scope.split(':', 1)
        if body['type'] == 'domainwithlink':
          body['withLink'] = True
          body['type'] = 'domain'
        if body['type'] not in DRIVEFILE_ACL_PERMISSION_TYPES[1:]:
          return None
        if body['type'] != 'domain':
          body[API.DRIVE_PERMISSIONS_GROUP_USER_TYPE_VALUE] = value
        else:
          body[API.DRIVE_PERMISSIONS_DOMAIN_TYPE_VALUE] = value
      _setRoleConvertCommenterToReader(body, DRIVEFILE_ACL_ROLES_MAP.get(role))
      if not body['role']:
        return None
      if expiration:
        body[API.DRIVE_PERMISSIONS_EXPIRATION_DATE_TIME] = expiration
      return body
    except ValueError:
      return None

  def _callbackAddPermission(request_id, response, exception):
    ri = request_id.splitlines()
    if int(ri[RI_J]) == 1:
      entityPerformActionNumItems([Ent.DRIVE_FILE_OR_FOLDER_ID, ri[RI_ENTITY]], int(ri[RI_JCOUNT]), Ent.PERMITTEE, int(ri[RI_I]), int(ri[RI_COUNT]))
    if exception is None:
      entityActionPerformed([Ent.DRIVE_FILE_OR_FOLDER_ID, ri[RI_ENTITY], Ent.PERMITTEE, ri[RI_ITEM]], int(ri[RI_J]), int(ri[RI_JCOUNT]))
    else:
      http_status, reason, message = checkGAPIError(exception)
      if reason not in GAPI.DEFAULT_RETRY_REASONS+[GAPI.SERVICE_LIMIT]:
        errMsg = getHTTPError({}, http_status, reason, message)
        entityActionFailedWarning([Ent.DRIVE_FILE_OR_FOLDER_ID, ri[RI_ENTITY], Ent.PERMITTEE, ri[RI_ITEM]], errMsg, int(ri[RI_J]), int(ri[RI_JCOUNT]))
        return
      waitOnFailure(1, 10, reason, message)
      try:
        callGAPI(drive.permissions(), API.DRIVE_CREATE_PERMISSIONS,
                 throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR, GAPI.INVALID_SHARING_REQUEST], retry_reasons=[GAPI.SERVICE_LIMIT],
                 fileId=ri[RI_ENTITY], sendNotificationEmails=sendNotificationEmails, emailMessage=emailMessage, body=_makePermissionBody(ri[RI_ITEM]), fields='')
        entityActionPerformed([Ent.DRIVE_FILE_OR_FOLDER_ID, ri[RI_ENTITY], Ent.PERMITTEE, ri[RI_ITEM]], int(ri[RI_J]), int(ri[RI_JCOUNT]))
      except (GAPI.fileNotFound, GAPI.invalidSharingRequest, GAPI.forbidden, GAPI.serviceNotAvailable, GAPI.authError) as e:
        entityActionFailedWarning([Ent.DRIVE_FILE_OR_FOLDER_ID, ri[RI_ENTITY], Ent.PERMITTEE, ri[RI_ITEM]], e.message, int(ri[RI_J]), int(ri[RI_JCOUNT]))

  sendNotificationEmails = False
  emailMessage = expiration = None
  fileIdEntity = getDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  permissions = getEntityList(Cmd.OB_DRIVE_FILE_PERMISSION_ENTITY)
  permissionsLists = permissions if isinstance(permissions, dict) else None
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if myarg == 'expiration':
      expiration = getFullTime()
    elif myarg == 'sendemail':
      sendNotificationEmails = True
    elif myarg == 'emailmessage':
      sendNotificationEmails = True
      emailMessage = getString(Cmd.OB_STRING)
    else:
      unknownArgumentExit()
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    origUser = user
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, entityType=Ent.DRIVE_FILE_OR_FOLDER_ACL)
    if jcount == 0:
      continue
    try:
      callGAPI(drive.about(), 'get',
               throw_reasons=GAPI.DRIVE_THROW_REASONS,
               fields='')
    except (GAPI.serviceNotAvailable, GAPI.authError):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)
      continue
    Ind.Increment()
    svcargs = dict([('fileId', None), ('sendNotificationEmails', sendNotificationEmails), ('emailMessage', emailMessage), ('body', None), ('fields', '')]+GM.Globals[GM.EXTRA_ARGS_LIST])
    dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackAddPermission)
    bcount = 0
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      if permissionsLists:
        if not GM.Globals[GM.CSV_SUBKEY_FIELD]:
          permissions = permissionsLists[fileId]
        else:
          permissions = permissionsLists[origUser][fileId]
      kcount = len(permissions)
      if kcount == 0:
        continue
      k = 0
      for permission in permissions:
        k += 1
        svcparms = svcargs.copy()
        svcparms['fileId'] = fileId
        svcparms['body'] = _makePermissionBody(permission)
        if not svcparms['body']:
          entityActionFailedWarning([Ent.DRIVE_FILE_OR_FOLDER_ID, fileId, Ent.PERMITTEE, permission], Msg.INVALID, k, kcount)
          continue
        dbatch.add(drive.permissions().insert(**svcparms), request_id=batchRequestID(fileId, j, jcount, k, kcount, permission))
        bcount += 1
        if bcount >= GC.Values[GC.BATCH_SIZE]:
          dbatch.execute()
          dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackAddPermission)
          bcount = 0
    if bcount > 0:
      dbatch.execute()
    Ind.Decrement()

