def _printShowDriveFileACLs(users, csvFormat):
  if csvFormat:
    todrive = {}
    titles, csvRows = initializeTitlesCSVfile(['Owner', 'id'])
  fileIdEntity = getDriveFileEntity()
  body, parameters = initializeDriveFileAttributes()
  showTitles = False
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if csvFormat and myarg == 'todrive':
      todrive = getTodriveParameters()
    elif myarg == 'showtitles':
      showTitles = True
      if csvFormat:
        addTitlesToCSVfile([API.DRIVE_FILE_NAME], titles)
    else:
      unknownArgumentExit()
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, drive, jcount = _validateUserGetFileIDs(user, i, count, fileIdEntity, body, parameters, entityType=[Ent.DRIVE_FILE_OR_FOLDER_ACL, None][csvFormat])
    if jcount == 0:
      continue
    Ind.Increment()
    j = 0
    for fileId in fileIdEntity['list']:
      j += 1
      try:
        fileName = fileId
        entityType = Ent.DRIVE_FILE_OR_FOLDER_ID
        if showTitles:
          result = callGAPI(drive.files(), 'get',
                            throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR],
                            fileId=fileId, fields=API.DRIVE_FILE_NAME)
          if result and API.DRIVE_FILE_NAME in result:
            entityType = Ent.DRIVE_FILE_OR_FOLDER
            fileName = result[API.DRIVE_FILE_NAME]
            if not csvFormat:
              fileName += '('+fileId+')'
        result = callGAPI(drive.permissions(), 'list',
                          throw_reasons=GAPI.DRIVE_THROW_REASONS+[GAPI.FILE_NOT_FOUND, GAPI.FORBIDDEN, GAPI.INTERNAL_ERROR],
                          fileId=fileId, fields=API.DRIVE_PERMISSIONS_LIST)
        if not csvFormat:
          printEntityKVList([entityType, fileName], [Ent.Plural(Ent.PERMITTEE)], j, jcount)
          if result:
            Ind.Increment()
            for permission in result[API.DRIVE_PERMISSIONS_LIST]:
              _showDriveFilePermission(permission)
            Ind.Decrement()
        elif result:
          result['permissions'] = result.pop('items', {})
          if showTitles:
            addRowTitlesToCSVfile(flattenJSON(result, flattened={'Owner': user, 'id': fileId, API.DRIVE_FILE_NAME: fileName}), csvRows, titles)
          else:
            addRowTitlesToCSVfile(flattenJSON(result, flattened={'Owner': user, 'id': fileId}), csvRows, titles)
      except (GAPI.fileNotFound, GAPI.forbidden, GAPI.internalError) as e:
        entityActionFailedWarning([Ent.USER, user, entityType, fileName], e.message, j, jcount)
      except (GAPI.serviceNotAvailable, GAPI.authError):
        entityServiceNotApplicableWarning(Ent.USER, user, i, count)
        break
    Ind.Decrement()
  if csvFormat:
    sortCSVTitles(['Owner', 'id', API.DRIVE_FILE_NAME], titles)
    writeCSVfile(csvRows, titles, 'Drive File ACLs', todrive)

