# gam <UserTypeEntity> add group|groups [member|manager|owner] <GroupEntity>
def addUserToGroups(users):

  _ADD_USER_REASON_TO_MESSAGE_MAP = {GAPI.DUPLICATE: Msg.DUPLICATE, GAPI.MEMBER_NOT_FOUND: Msg.DOES_NOT_EXIST, GAPI.INVALID_MEMBER: Msg.INVALID_ROLE}
  def _callbackAddUserToGroups(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is None:
      if str(response['email']).lower() != ri[RI_ITEM]:
        entityActionPerformed([Ent.GROUP, ri[RI_ENTITY], ri[RI_ROLE], '{0} (primary address)'.format(response['email'])], int(ri[RI_J]), int(ri[RI_JCOUNT]))
      else:
        entityActionPerformed([Ent.GROUP, ri[RI_ENTITY], ri[RI_ROLE], response['email']], int(ri[RI_J]), int(ri[RI_JCOUNT]))
    else:
      http_status, reason, message = checkGAPIError(exception)
      if reason in GAPI.MEMBERS_THROW_REASONS:
        entityUnknownWarning(Ent.GROUP, ri[RI_ENTITY], int(ri[RI_J]), int(ri[RI_JCOUNT]))
      else:
        errMsg = getHTTPError(_ADD_USER_REASON_TO_MESSAGE_MAP, http_status, reason, message)
        entityActionFailedWarning([Ent.GROUP, ri[RI_ENTITY], ri[RI_ROLE], ri[RI_ITEM]], errMsg, int(ri[RI_J]), int(ri[RI_JCOUNT]))

  def _batchAddUserToGroups(cd, user, i, count, groupKeys, body):
    Act.Set(Act.ADD)
    role = body['role']
    jcount = len(groupKeys)
    entityPerformActionModifierNumItemsModifier([Ent.USER, user], Act.MODIFIER_TO, jcount, Ent.GROUP, '{0} {1}'.format(Msg.AS, body['role'].lower()), i, count)
    Ind.Increment()
    svcargs = dict([('groupKey', None), ('body', body), ('fields', 'email')]+GM.Globals[GM.EXTRA_ARGS_LIST])
    dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackAddUserToGroups)
    bcount = 0
    j = 0
    for group in groupKeys:
      j += 1
      svcparms = svcargs.copy()
      svcparms['groupKey'] = normalizeEmailAddressOrUID(group)
      dbatch.add(cd.members().insert(**svcparms), request_id=batchRequestID(svcparms['groupKey'], 0, 0, j, jcount, user, role))
      bcount += 1
      if bcount >= GC.Values[GC.BATCH_SIZE]:
        dbatch.execute()
        dbatch = googleapiclient.http.BatchHttpRequest(callback=_callbackAddUserToGroups)
        bcount = 0
    if bcount > 0:
      dbatch.execute()
    Ind.Decrement()

  cd = buildGAPIObject(API.DIRECTORY)
  role = getChoice(GROUP_ROLES_MAP, defaultChoice=Ent.ROLE_MEMBER, mapChoice=True)
  groupKeys = getEntityList(Cmd.OB_GROUP_ENTITY)
  userGroupLists = groupKeys if isinstance(groupKeys, dict) else None
  checkForExtraneousArguments()
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    if userGroupLists:
      groupKeys = userGroupLists[user]
    user = checkUserExists(cd, user, i, count)
    if user:
      _batchAddUserToGroups(cd, user, i, count, groupKeys, {'role': role, 'email': user})

