  fields = ','.join(['clientId', 'displayText', 'anonymous', 'nativeApp', 'userKey', 'scopes'])
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user = normalizeEmailAddressOrUID(user)
    try:
      if csvFormat:
        printGettingEntityItemForWhom(Ent.ACCESS_TOKEN, user, i, count)
      if clientId:
        results = [callGAPI(cd.tokens(), 'get',
                            throw_reasons=[GAPI.USER_NOT_FOUND, GAPI.DOMAIN_NOT_FOUND, GAPI.FORBIDDEN, GAPI.NOT_FOUND, GAPI.RESOURCE_NOT_FOUND],
                            userKey=user, clientId=clientId, fields=fields)]
      else:
        results = callGAPIitems(cd.tokens(), 'list', 'items',
                                throw_reasons=[GAPI.USER_NOT_FOUND, GAPI.DOMAIN_NOT_FOUND, GAPI.FORBIDDEN],
                                userKey=user, fields='items({0})'.format(fields))
      jcount = len(results)
      if not csvFormat:
        entityPerformActionNumItems([Ent.USER, user], jcount, Ent.ACCESS_TOKEN, i, count)
      if jcount == 0:
        setSysExitRC(NO_ENTITIES_FOUND)
        continue
      if not csvFormat:
        Ind.Increment()
        j = 0
        for token in results:
          j += 1
          _showToken(token, j, jcount)
        Ind.Decrement()
      else:
        for token in results:
          row = {'user': user, 'scopes': delimiter.join(token.get('scopes', []))}
          for item in token:
            if item != 'scopes':
              row[item] = token.get(item, '')
          csvRows.append(row)
    except (GAPI.notFound, GAPI.resourceNotFound) as e:
      entityActionFailedWarning([Ent.USER, user, Ent.ACCESS_TOKEN, clientId], e.message, i, count)
    except (GAPI.userNotFound, GAPI.domainNotFound, GAPI.forbidden):
      entityUnknownWarning(Ent.USER, user, i, count)
  if csvFormat:
    writeCSVfile(csvRows, titles, 'OAuth Tokens', todrive)

