def _printShowMessagesThreads(users, entityType, csvFormat):

  _GMAIL_ERROR_REASON_TO_MESSAGE_MAP = {GAPI.NOT_FOUND: Msg.DOES_NOT_EXIST, GAPI.INVALID_MESSAGE_ID: Msg.INVALID_MESSAGE_ID}
  def _handleShowGmailError(exception, ri):
    http_status, reason, message = checkGAPIError(exception)
    errMsg = getHTTPError(_GMAIL_ERROR_REASON_TO_MESSAGE_MAP, http_status, reason, message)
    printKeyValueListWithCount([Ent.Singular(entityType), ri[RI_ITEM], errMsg], int(ri[RI_J]), int(ri[RI_JCOUNT]))
    setSysExitRC(AC_FAILED_RC)

  def _handlePrintGmailError(exception, ri):
    http_status, reason, message = checkGAPIError(exception)
    errMsg = getHTTPError(_GMAIL_ERROR_REASON_TO_MESSAGE_MAP, http_status, reason, message)
    entityActionFailedWarning([Ent.USER, ri[RI_ENTITY], entityType, ri[RI_ITEM]], errMsg, int(ri[RI_J]), int(ri[RI_JCOUNT]))

  def _getMessageBody(payload):
    for header in payload['headers']:
      if header['name'].lower() == 'content-type':
        if header['value'].startswith('multipart/mixed') or header['value'].startswith('multipart/related'):
          return _getMessageBody(payload['parts'][0])
        break
    if payload['body']['size']:
      return dehtml(base64.urlsafe_b64decode(str(payload['body']['data'])))
    for part in payload['parts']:
      if part['mimeType'] == 'text/plain':
        return dehtml(base64.urlsafe_b64decode(str(part['body']['data'])))
    return 'Body not available'

  def _showMessage(result, j, jcount):
    printEntity([Ent.MESSAGE, result['id']], j, jcount)
    Ind.Increment()
    if show_snippet:
      printKeyValueList(['Snippet', dehtml(result['snippet']).replace('\n', ' ')])
    for name in headersToShow:
      for header in result['payload']['headers']:
        if name == header['name'].lower():
          printKeyValueList([SMTP_HEADERS_MAP.get(name, header['name']), header['value']])
    if show_size:
      printKeyValueList(['SizeEstimate', result['sizeEstimate']])
    if show_labels:
      messageLabels = []
      for labelId in result['labelIds']:
        for label in labels['labels']:
          if label['id'] == labelId:
            messageLabels.append(label['name'])
            break
      printKeyValueList(['Labels', ','.join(messageLabels)])
    if show_body:
      printKeyValueList(['Body', None])
      Ind.Increment()
      printKeyValueList([Ind.MultiLineText(_getMessageBody(result['payload']))])
      Ind.Decrement()
    Ind.Decrement()

  def _callbackShowMessage(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is  None:
      _showMessage(response, int(ri[RI_J]), int(ri[RI_JCOUNT]))
    else:
      _handleShowGmailError(exception, ri)

  def _printMessage(user, result):
    row = {'User': user, 'threadId': result['threadId'], 'id': result['id']}
    if show_snippet:
      row['Snippet'] = dehtml(result['snippet']).replace('\n', ' ')
    for name in headersToShow:
      j = 0
      for header in result['payload']['headers']:
        if name == header['name'].lower():
          j += 1
          if j == 1:
            row[SMTP_HEADERS_MAP.get(name, header['name'])] = header['value']
          else:
            row['{0} {1}'.format(SMTP_HEADERS_MAP.get(name, header['name']), j)] = header['value']
    if show_size:
      row['SizeEstimate'] = result['sizeEstimate']
    if show_labels:
      messageLabels = []
      for labelId in result['labelIds']:
        for label in labels['labels']:
          if label['id'] == labelId:
            messageLabels.append(label['name'])
            break
      row['Labels'] = delimiter.join(messageLabels)
    if show_body:
      if not convertCRNL:
        row['Body'] = _getMessageBody(result['payload'])
      else:
        row['Body'] = _getMessageBody(convertCRsNLs(result['payload']))
    addRowTitlesToCSVfile(row, csvRows, titles)

  def _callbackPrintMessage(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is None:
      _printMessage(ri[RI_ENTITY], response)
    else:
      _handlePrintGmailError(exception, ri)

  def _showThread(result, j, jcount):
    printEntity([Ent.THREAD, result['id']], j, jcount)
    Ind.Increment()
    if show_snippet and 'snippet' in result:
      printKeyValueList(['Snippet', dehtml(result['snippet']).replace('\n', ' ')])
    try:
      result = callGAPI(service, 'get',
                        throw_reasons=GAPI.GMAIL_THROW_REASONS+[GAPI.NOT_FOUND],
                        id=result['id'], userId='me', format='metadata')
      kcount = len(result['messages'])
      k = 0
      for message in result['messages']:
        k += 1
        _showMessage(message, k, kcount)
    except (GAPI.serviceNotAvailable, GAPI.badRequest, GAPI.notFound):
      pass
    Ind.Decrement()

  def _callbackShowThread(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is None:
      _showThread(response, int(ri[RI_J]), int(ri[RI_JCOUNT]))
    else:
      _handleShowGmailError(exception, ri)

  def _printThread(user, result):
    try:
      result = callGAPI(service, 'get',
                        throw_reasons=GAPI.GMAIL_THROW_REASONS+[GAPI.NOT_FOUND],
                        id=result['id'], userId='me', format='metadata')
      for message in result['messages']:
        _printMessage(user, message)
    except (GAPI.serviceNotAvailable, GAPI.badRequest, GAPI.notFound):
      pass

  def _callbackPrintThread(request_id, response, exception):
    ri = request_id.splitlines()
    if exception is None:
      _printThread(ri[RI_ENTITY], response)
    else:
      _handlePrintGmailError(exception, ri)

  def _batchPrintShowMessagesThreads(service, user, jcount, messageIds, callback):
    svcargs = dict([('userId', 'me'), ('id', None), ('format', ['metadata', 'full'][show_body])]+GM.Globals[GM.EXTRA_ARGS_LIST])
    dbatch = googleapiclient.http.BatchHttpRequest(callback=callback)
    bcount = 0
    j = 0
    for messageId in messageIds:
      j += 1
      svcparms = svcargs.copy()
      svcparms['id'] = messageId
      dbatch.add(service.get(**svcparms), request_id=batchRequestID(user, 0, 0, j, jcount, svcparms['id']))
      bcount += 1
      if maxToProcess and j == maxToProcess:
        break
      if bcount == GC.Values[GC.EMAIL_BATCH_SIZE]:
        dbatch.execute()
        dbatch = googleapiclient.http.BatchHttpRequest(callback=callback)
        bcount = 0
    if bcount > 0:
      dbatch.execute()

  currLabelOp = prevLabelOp = 'and'
  labelGroupOpen = False
  query = ''
  includeSpamTrash = False
  quick = True
  maxToProcess = 0
  convertCRNL = GC.Values[GC.CSV_OUTPUT_CONVERT_CR_NL]
  delimiter = GC.Values[GC.CSV_OUTPUT_FIELD_DELIMITER]
  show_body = show_labels = show_size = show_snippet = False
  messageEntity = None
  headersToShow = ['Date', 'Subject', 'From', 'Reply-To', 'To', 'Delivered-To', 'Content-Type', 'Message-ID']
  if csvFormat:
    todrive = {}
    titles, csvRows = initializeTitlesCSVfile(['User', 'threadId', 'id'])
  while Cmd.ArgumentsRemaining():
    myarg = getArgument()
    if csvFormat and myarg == 'todrive':
      todrive = getTodriveParameters()
    elif myarg == 'query':
      query += ' ({0})'.format(getString(Cmd.OB_QUERY))
    elif myarg == 'matchlabel':
      labelName = getString(Cmd.OB_LABEL_NAME).lower().replace(' ', '-').replace('/', '-')
      if not labelGroupOpen:
        query += '('
        labelGroupOpen = True
      query += ' label:{0}'.format(labelName)
    elif myarg in ['or', 'and']:
      prevLabelOp = currLabelOp
      currLabelOp = myarg
      if labelGroupOpen and currLabelOp != prevLabelOp:
        query += ')'
        labelGroupOpen = False
      if currLabelOp == 'or':
        query += ' OR '
    elif myarg == 'ids':
      messageEntity = getUserObjectEntity(Cmd.OB_MESSAGE_ID, entityType)
    elif myarg == 'quick':
      quick = True
    elif myarg == 'notquick':
      quick = False
    elif myarg in['maxtoprint', 'maxtoshow', 'maxtoprocess']:
      maxToProcess = getInteger(minVal=0)
    elif myarg == 'headers':
      headersToShow = getString(Cmd.OB_STRING, minLen=0).replace(',', ' ').split()
    elif myarg in ['convertcrnl', 'converttextnl', 'convertbodynl']:
      convertCRNL = True
    elif myarg == 'showbody':
      show_body = True
    elif myarg == 'showlabels':
      show_labels = True
    elif myarg == 'showsize':
      show_size = True
    elif myarg == 'showsnippet':
      show_snippet = True
    elif myarg == 'includespamtrash':
      includeSpamTrash = True
    elif myarg == 'delimiter':
      delimiter = getDelimiter()
    else:
      unknownArgumentExit()
  if query:
    if labelGroupOpen:
      query += ')'
  else:
    query = None
  if csvFormat:
    headerTitles = []
    for j, name in enumerate(headersToShow):
      headersToShow[j] = name.lower()
      headerTitles.append(SMTP_HEADERS_MAP.get(headersToShow[j], name))
    addTitlesToCSVfile(headerTitles, titles)
  else:
    for j, name in enumerate(headersToShow):
      headersToShow[j] = name.lower()
  listType = ['threads', 'messages'][entityType == Ent.MESSAGE]
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, gmail, messageIds = _validateUserGetMessageIds(user, messageEntity)
    if not gmail:
      continue
    service = [gmail.users().threads(), gmail.users().messages()][entityType == Ent.MESSAGE]
    try:
      if show_labels:
        labels = _getUserGmailLabels(gmail, user, i, count, fields='labels(id,name)')
        if not labels:
          continue
      if messageEntity is None:
        printGettingAllEntityItemsForWhom(entityType, user, i, count)
        page_message = getPageMessage()
        listResult = callGAPIpages(service, 'list', listType,
                                   page_message=page_message, maxItems=[0, maxToProcess][quick],
                                   throw_reasons=GAPI.GMAIL_THROW_REASONS,
                                   userId='me', q=query, includeSpamTrash=includeSpamTrash, fields='nextPageToken,{0}(id)'.format(listType), maxResults=GC.Values[GC.MESSAGE_MAX_RESULTS])
        messageIds = [message['id'] for message in listResult]
      else:
        # Need to get authorization set up for batch
        callGAPI(gmail.users(), 'getProfile',
                 throw_reasons=GAPI.GMAIL_THROW_REASONS,
                 userId='me', fields='')
      jcount = len(messageIds)
      if jcount == 0:
        if not csvFormat:
          entityNumEntitiesActionNotPerformedWarning(Ent.USER, user, entityType, jcount, Msg.NO_ENTITIES_MATCHED.format(Ent.Plural(entityType)), i, count)
        setSysExitRC(NO_ENTITIES_FOUND)
        continue
      if not csvFormat:
        if messageEntity is not None or maxToProcess == 0 or jcount <= maxToProcess:
          entityPerformActionNumItems([Ent.USER, user], jcount, entityType, i, count)
        else:
          entityPerformActionNumItemsModifier([Ent.USER, user], maxToProcess, entityType, 'of {0} Total {1}'.format(jcount, Ent.Plural(entityType)), i, count)
      if messageEntity is None and maxToProcess and (jcount > maxToProcess):
        jcount = maxToProcess
      if not csvFormat:
        Ind.Increment()
        _batchPrintShowMessagesThreads(service, user, jcount, messageIds, [_callbackShowThread, _callbackShowMessage][entityType == Ent.MESSAGE])
        Ind.Decrement()
      else:
        _batchPrintShowMessagesThreads(service, user, jcount, messageIds, [_callbackPrintThread, _callbackPrintMessage][entityType == Ent.MESSAGE])
    except (GAPI.serviceNotAvailable, GAPI.badRequest):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)
  if csvFormat:
    removeTitlesFromCSVfile(['Snippet', 'SizeEstimate', 'Labels', 'Body'], titles)
    if show_snippet:
      addTitleToCSVfile('Snippet', titles)
    if show_size:
      addTitleToCSVfile('SizeEstimate', titles)
    if show_labels:
      addTitleToCSVfile('Labels', titles)
    if show_body:
      addTitleToCSVfile('Body', titles)
    writeCSVfile(csvRows, titles, 'Messages', todrive)

