def _printShowDelegates(users, csvFormat):

  def getDelegateFields(delegate):
    delegateName = ''
    delegateAddress = ''
    delegationId = ''
    delegateStatus = ''
    for item in delegate['apps$property']:
      if item['name'] == 'delegate':
        delegateName = item['value']
      elif item['name'] == 'address':
        delegateAddress = item['value']
      elif item['name'] == 'delegationId':
        delegationId = item['value']
      elif item['name'] == 'status':
        delegateStatus = item['value']
    return delegateName, delegateAddress, delegationId, delegateStatus

