#	[contactsonly] [domainonly] [startdate <Date>] [enddate <Date>]
def setVacation(users):
  enable = getBoolean()
  body = {'enableAutoReply': enable}
  if enable:
    responseBodyType = 'responseBodyPlainText'
    message = None
    tagReplacements = {}
    while Cmd.ArgumentsRemaining():
      myarg = getArgument()
      if myarg == 'subject':
        body['responseSubject'] = getString(Cmd.OB_STRING, checkBlank=True)
      elif myarg == 'message':
        message = getString(Cmd.OB_STRING, checkBlank=True)
      elif myarg == 'file':
        filename = getString(Cmd.OB_FILE_NAME)
        encoding = getCharSet()
        message = readFile(filename, encoding=encoding)
      elif myarg == 'replace':
        matchTag = getString(Cmd.OB_TAG)
        matchReplacement = getString(Cmd.OB_STRING, minLen=0)
        tagReplacements[matchTag] = matchReplacement
      elif myarg == 'html':
        responseBodyType = 'responseBodyHtml'
      elif myarg == 'contactsonly':
        body['restrictToContacts'] = True
      elif myarg == 'domainonly':
        body['restrictToDomain'] = True
      elif myarg == 'startdate':
        body['startTime'] = getYYYYMMDD(returnTimeStamp=True)
      elif myarg == 'enddate':
        body['endTime'] = getYYYYMMDD(returnTimeStamp=True)
      else:
        unknownArgumentExit()
    if message:
      if responseBodyType == 'responseBodyHtml':
        message = message.replace('\r', '').replace('\\n', '<br/>')
      else:
        message = message.replace('\r', '').replace('\\n', '\n')
      if tagReplacements:
        message = _processTags(tagReplacements, message)
      body[responseBodyType] = message
    if not message and not body.get('responseSubject'):
      missingArgumentExit('message or subject')
  i, count, users = getEntityArgument(users)
  for user in users:
    i += 1
    user, gmail = buildGAPIServiceObject(API.GMAIL, user)
    if not gmail:
      continue
    try:
      result = callGAPI(gmail.users().settings(), 'updateVacation',
                        throw_reasons=GAPI.GMAIL_THROW_REASONS+[GAPI.INVALID_ARGUMENT],
                        userId='me', body=body)
      printEntity([Ent.USER, user, Ent.VACATION_ENABLED, result['enableAutoReply']], i, count)
    except GAPI.invalidArgument as e:
      entityActionFailedWarning([Ent.USER, user, Ent.VACATION_ENABLED, enable], e.message, i, count)
    except (GAPI.serviceNotAvailable, GAPI.badRequest):
      entityServiceNotApplicableWarning(Ent.USER, user, i, count)

