          aliasdomain[u'type'] = u'alias'
          row = {}
          for attr in aliasdomain:
            if attr in [u'kind', u'etag']:
              continue
            if attr == u'creationTime':
              aliasdomain[attr] = formatLocalTimestamp(aliasdomain[attr])
            row[attr] = aliasdomain[attr]
