        if checkArgumentPresent(Cmd.CLEAR_NONE_ARGUMENT):
          ClearFieldsList(fieldName)
          continue
        entry = InitArrayItem(ContactsManager.EMAIL_TYPE_ARGUMENT_TO_REL)
