      http_status, reason, message = checkGAPIError(exception)
      if reason == GAPI.NOT_FOUND and ri[RI_ROLE] != Ent.COURSE_ALIAS:
        errMsg = u'{0} {1}'.format(Msg.NOT_A, Ent.Singular(ri[RI_ROLE]))
