def _mapDriveFieldNames(f_file, user, mapToLabels=False):
  if mapToLabels:
    for attrib in API.DRIVE3_TO_DRIVE2_LABELS_MAP:
      if attrib in f_file:
        f_file.setdefault(u'labels', {})
        if attrib != u'viewersCanCopyContent':
          f_file[u'labels'][API.DRIVE3_TO_DRIVE2_LABELS_MAP[attrib]] = f_file[attrib]
        else:
          f_file[u'labels'][API.DRIVE3_TO_DRIVE2_LABELS_MAP[attrib]] = not f_file[attrib]
        del f_file[attrib]
  for attrib in API.DRIVE3_TO_DRIVE2_FILES_FIELDS_MAP:
    if attrib in f_file:
      if attrib != u'viewersCanCopyContent':
        f_file[API.DRIVE3_TO_DRIVE2_FILES_FIELDS_MAP[attrib]] = f_file[attrib]
      else:
        f_file[API.DRIVE3_TO_DRIVE2_FILES_FIELDS_MAP[attrib]] = not f_file[attrib]
      del f_file[attrib]
  capabilities = f_file.get(u'capabilities')
  if capabilities:
    for attrib in API.DRIVE3_TO_DRIVE2_CAPABILITIES_FIELDS_MAP:
      if attrib in capabilities:
        f_file[API.DRIVE3_TO_DRIVE2_CAPABILITIES_FIELDS_MAP[attrib]] = capabilities[attrib]
    for attrib in API.DRIVE3_TO_DRIVE2_CAPABILITIES_NAMES_MAP:
      if attrib in capabilities:
        capabilities[API.DRIVE3_TO_DRIVE2_CAPABILITIES_NAMES_MAP[attrib]] = capabilities[attrib]
        del capabilities[attrib]
  if u'spaces' in f_file:
    f_file[u'appDataContents'] = u'appDataFolder' in f_file[u'spaces']
  if u'lastModifyingUser' in f_file:
    if u'displayName' in f_file[u'lastModifyingUser']:
      f_file[u'lastModifyingUserName'] = f_file[u'lastModifyingUser'][u'displayName']
    _mapDriveUser(f_file[u'lastModifyingUser'])
  if u'owners' in f_file:
    for owner in f_file[u'owners']:
      _mapDriveUser(owner)
    f_file[u'ownerNames'] = [owner[u'displayName'] for owner in f_file[u'owners']]
  _mapDriveUser(f_file.get(u'sharingUser', {}))
  if u'parents' in f_file:
    parents = f_file[u'parents'][:]
    f_file[u'parents'] = []
    for parent in parents:
      f_file[u'parents'].append({u'id': parent})
  for permission in f_file.get(u'permissions', []):
    if (permission[u'type'] == u'user') and (permission[u'emailAddress'].lower() == user):
      f_file[u'userPermission'] = {u'id': u'me', u'role': permission[u'role'], u'type': permission[u'type']}
    _mapDrivePermissionNames(permission)
  if u'properties' in f_file:
    properties = f_file[u'properties'].copy()
    f_file[u'properties'] = []
    for key, value in iteritems(properties):
      if key != u'appProperties':
        f_file[u'properties'].append({u'key': key, u'value': value, u'visibility': True})
      else:
        for pkey, pvalue in iteritems(value):
          f_file[u'properties'].append({u'key': pkey, u'value': pvalue, u'visibility': False})

