        Ind.Increment()
        if filepath:
          _, paths = getFilePaths(drive, None, result, filePathInfo)
          kcount = len(paths)
          printKeyValueList([u'paths', kcount])
          Ind.Increment()
          for path in paths:
            printKeyValueList([u'path', path])
